INSERT INTO Question (id, text, options, correct, explanation, categoryId, seriesId) VALUES
('q-erasmus-a1-001', 'My name _______ John.', '["am","is","are","be"]', 'B', 'With singular subjects like "name", we use "is".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-002', 'I _______ from Turkey.', '["am","is","are","be"]', 'A', 'With "I", we always use "am".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-003', 'She _______ a student.', '["am","is","are","be"]', 'B', 'With "she", we use "is".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-004', 'We _______ happy.', '["am","is","are","be"]', 'C', 'With "we", we use "are".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-005', 'This _______ my book.', '["am","is","are","be"]', 'B', 'With "this", we use "is".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-006', '_______ you a teacher?', '["Am","Is","Are","Be"]', 'C', 'For questions with "you", we use "Are".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-007', 'They _______ my friends.', '["am","is","are","be"]', 'C', 'With "they", we use "are".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-008', 'The cat _______ black.', '["am","is","are","be"]', 'B', 'With singular animals like "cat", we use "is".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-009', 'I _______ 20 years old.', '["am","is","are","be"]', 'A', 'With "I", we always use "am".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7'),
('q-erasmus-a1-010', 'English _______ easy.', '["am","is","are","be"]', 'B', 'With singular subjects like "English", we use "is".', 1, 'e3bfe6e3-c4d8-434c-8d1c-0862144d20a7');
