import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, Paper, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';

interface FillInTheBlanksQuestion {
  id: string;
  text: string;
  options: string[];
  correctAnswers: string[];
  explanation: string;
}

const FillInTheBlanksGame: React.FC = () => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState<FillInTheBlanksQuestion[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState<(string | null)[]>([]);
  const [availableOptions, setAvailableOptions] = useState<string[]>([]);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);
  const [draggedItem, setDraggedItem] = useState<string | null>(null);

  // Backend'den soruları çek
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const response = await fetch('http://localhost:4000/api/games/fill-in-the-blanks/questions');
        if (response.ok) {
          const data = await response.json();
          setQuestions(data);
          if (data.length > 0) {
            resetCurrentQuestion(data[0]);
          }
        } else {
          console.error('Failed to fetch questions');
          // Fallback to sample data if backend is not available
          const sampleQuestions: FillInTheBlanksQuestion[] = [
            {
              id: 'fill-1',
              text: 'I __________ from Turkey. My name __________ John. She __________ a student. We __________ happy today. They __________ at home.',
              options: ['am', 'is', 'are', 'was', 'were'],
              correctAnswers: ['am', 'is', 'is', 'are', 'are'],
              explanation: 'Bu soruda "to be" fiilinin doğru kullanımları test ediliyor.'
            }
          ];
          setQuestions(sampleQuestions);
          resetCurrentQuestion(sampleQuestions[0]);
        }
      } catch (error) {
        console.error('Error fetching questions:', error);
        // Fallback to sample data
        const sampleQuestions: FillInTheBlanksQuestion[] = [
          {
            id: 'fill-1',
            text: 'I __________ from Turkey. My name __________ John. She __________ a student. We __________ happy today. They __________ at home.',
            options: ['am', 'is', 'are', 'was', 'were'],
            correctAnswers: ['am', 'is', 'is', 'are', 'are'],
            explanation: 'Bu soruda "to be" fiilinin doğru kullanımları test ediliyor.'
          }
        ];
        setQuestions(sampleQuestions);
        resetCurrentQuestion(sampleQuestions[0]);
      }
    };

    fetchQuestions();
  }, []);

  const resetCurrentQuestion = (question: FillInTheBlanksQuestion) => {
    setUserAnswers(Array(question.correctAnswers.length).fill(null));
    setAvailableOptions([...question.options]);
    setIsSubmitted(false);
    setShowResults(false);
  };

  const currentQuestion = questions[currentIndex];

  const handleDragStart = (e: React.DragEvent, option: string) => {
    setDraggedItem(option);
    e.dataTransfer.effectAllowed = 'move';
  };

  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };

  const handleDrop = (e: React.DragEvent, blankIndex: number) => {
    e.preventDefault();
    if (draggedItem && !isSubmitted) {
      // Eğer bu boşlukta zaten bir cevap varsa, onu geri options'a ekle
      if (userAnswers[blankIndex]) {
        setAvailableOptions(prev => [...prev, userAnswers[blankIndex] as string]);
      }

      // Yeni cevabı ekle
      const newAnswers = [...userAnswers];
      newAnswers[blankIndex] = draggedItem;
      setUserAnswers(newAnswers);

      // Kullanılan seçeneği available options'dan çıkar
      setAvailableOptions(prev => prev.filter(option => option !== draggedItem));
      setDraggedItem(null);
    }
  };

  const handleRemoveAnswer = (blankIndex: number) => {
    if (userAnswers[blankIndex] && !isSubmitted) {
      setAvailableOptions(prev => [...prev, userAnswers[blankIndex] as string]);
      const newAnswers = [...userAnswers];
      newAnswers[blankIndex] = null;
      setUserAnswers(newAnswers);
    }
  };

  const handleSubmit = () => {
    setIsSubmitted(true);
    let correctCount = 0;
    currentQuestion.correctAnswers.forEach((correct, index) => {
      if (userAnswers[index] === correct) {
        correctCount++;
      }
    });
    setScore(correctCount);
    setShowResults(true);
  };

  const renderTextWithBlanks = () => {
    if (!currentQuestion) return null;

    // Metni {1}, {2}, {3} şeklinde placeholder'larla böl
    let text = currentQuestion.text;
    const placeholderRegex = /\{(\d+)\}/g;
    const matches = text.match(placeholderRegex);

    if (matches) {
      // Numbered placeholders {1}, {2}, {3} kullanılıyor
      const result: (string | React.ReactNode)[] = [];
      let lastIndex = 0;

      matches.forEach((match, index) => {
        const placeholderIndex = text.indexOf(match, lastIndex);

        // Placeholder'dan önceki metni ekle
        if (placeholderIndex > lastIndex) {
          result.push(text.substring(lastIndex, placeholderIndex));
        }

        const blankIndex = index; // Sıralı index kullan
        const userAnswer = userAnswers[blankIndex];
        const correctAnswer = currentQuestion.correctAnswers[blankIndex];
        const isCorrect = isSubmitted && userAnswer === correctAnswer;
        const isIncorrect = isSubmitted && userAnswer && userAnswer !== correctAnswer;

        result.push(
          <Box
            key={`blank-${index}`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, blankIndex)}
            onClick={() => handleRemoveAnswer(blankIndex)}
            sx={{
              display: 'inline-block',
              minWidth: '100px',
              minHeight: '40px',
              border: '2px dashed #ccc',
              borderRadius: '8px',
              margin: '0 8px',
              padding: '8px',
              textAlign: 'center',
              cursor: userAnswer && !isSubmitted ? 'pointer' : 'default',
              backgroundColor: isSubmitted 
                ? (isCorrect ? '#e8f5e8' : isIncorrect ? '#ffeaea' : '#f5f5f5')
                : userAnswer ? '#e3f2fd' : '#f9f9f9',
              borderColor: isSubmitted 
                ? (isCorrect ? '#4caf50' : isIncorrect ? '#f44336' : '#ccc')
                : userAnswer ? '#2196f3' : '#ccc',
              transition: 'all 0.3s ease',
              '&:hover': {
                backgroundColor: !isSubmitted && userAnswer ? '#bbdefb' : undefined,
              }
            }}
          >
            {userAnswer && (
              <Typography 
                variant="body1" 
                sx={{ 
                  fontWeight: 600,
                  color: isSubmitted 
                    ? (isCorrect ? '#2e7d32' : isIncorrect ? '#c62828' : '#666')
                    : '#1976d2'
                }}
              >
                {userAnswer}
              </Typography>
            )}
            {!userAnswer && (
              <Typography variant="body2" color="text.secondary">
                {index + 1} {/* Sıra numarasını göster */}
              </Typography>
            )}
          </Box>
        );

        lastIndex = placeholderIndex + match.length;
      });

      // Son kalan metni ekle
      if (lastIndex < text.length) {
        result.push(text.substring(lastIndex));
      }

      return result;
    } else {
      // Eski __________ formatı için fallback
      const parts = currentQuestion.text.split('__________');
      const result: (string | React.ReactNode)[] = [];

      parts.forEach((part, index) => {
        result.push(part);
        if (index < parts.length - 1) {
          const blankIndex = index;
          const userAnswer = userAnswers[blankIndex];
          const correctAnswer = currentQuestion.correctAnswers[blankIndex];
          const isCorrect = isSubmitted && userAnswer === correctAnswer;
          const isIncorrect = isSubmitted && userAnswer && userAnswer !== correctAnswer;

          result.push(
            <Box
              key={`blank-${index}`}
              onDragOver={handleDragOver}
              onDrop={(e) => handleDrop(e, blankIndex)}
              onClick={() => handleRemoveAnswer(blankIndex)}
              sx={{
                display: 'inline-block',
                minWidth: '100px',
                minHeight: '40px',
                border: '2px dashed #ccc',
                borderRadius: '8px',
                margin: '0 8px',
                padding: '8px',
                textAlign: 'center',
                cursor: userAnswer && !isSubmitted ? 'pointer' : 'default',
                backgroundColor: isSubmitted 
                  ? (isCorrect ? '#e8f5e8' : isIncorrect ? '#ffeaea' : '#f5f5f5')
                  : userAnswer ? '#e3f2fd' : '#f9f9f9',
                borderColor: isSubmitted 
                  ? (isCorrect ? '#4caf50' : isIncorrect ? '#f44336' : '#ccc')
                  : userAnswer ? '#2196f3' : '#ccc',
                transition: 'all 0.3s ease',
                '&:hover': {
                  backgroundColor: !isSubmitted && userAnswer ? '#bbdefb' : undefined,
                }
              }}
            >
              {userAnswer && (
                <Typography 
                  variant="body1" 
                  sx={{ 
                    fontWeight: 600,
                    color: isSubmitted 
                      ? (isCorrect ? '#2e7d32' : isIncorrect ? '#c62828' : '#666')
                      : '#1976d2'
                  }}
                >
                  {userAnswer}
                </Typography>
              )}
              {!userAnswer && (
                <Typography variant="body2" color="text.secondary">
                  {index + 1}
                </Typography>
              )}
            </Box>
          );
        }
      });

      return result;
    }
  };

  if (!currentQuestion) {
    return (
      <Box sx={{ p: 4, textAlign: 'center' }}>
        <Typography variant="h5">Boşluk Doldurma Oyunu</Typography>
        <Typography>Sorular yükleniyor...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      p: 4 
    }}>
      <Paper sx={{ maxWidth: 1000, mx: 'auto', p: 4 }}>
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography variant="h4" gutterBottom color="primary">
            Boşluk Doldurma Oyunu
          </Typography>
          <Typography variant="h6" color="text.secondary">
            Soru {currentIndex + 1} / {questions.length}
          </Typography>
          <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
            Kelimeleri sürükleyip boşluklara bırakın
          </Typography>
        </Box>

        <Paper sx={{ p: 3, mb: 3, backgroundColor: '#f8f9fa' }}>
          <Typography variant="body1" sx={{ fontSize: '1.2rem', lineHeight: 2, textAlign: 'center' }}>
            {renderTextWithBlanks()}
          </Typography>
        </Paper>

        {/* Available Options */}
        <Paper sx={{ p: 3, mb: 3, backgroundColor: '#fff' }}>
          <Typography variant="h6" gutterBottom color="primary">
            Seçenekler:
          </Typography>
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
            {availableOptions.map((option, index) => (
              <Box
                key={`option-${index}`}
                draggable={!isSubmitted}
                onDragStart={(e) => handleDragStart(e, option)}
                sx={{
                  padding: '8px 16px',
                  backgroundColor: '#e3f2fd',
                  border: '2px solid #2196f3',
                  borderRadius: '20px',
                  cursor: isSubmitted ? 'default' : 'grab',
                  userSelect: 'none',
                  opacity: isSubmitted ? 0.6 : 1,
                  transition: 'all 0.3s ease',
                  '&:hover': {
                    backgroundColor: !isSubmitted ? '#bbdefb' : undefined,
                    transform: !isSubmitted ? 'scale(1.05)' : undefined,
                  },
                  '&:active': {
                    cursor: !isSubmitted ? 'grabbing' : 'default',
                  }
                }}
              >
                <Typography variant="body1" fontWeight={600} color="primary">
                  {option}
                </Typography>
              </Box>
            ))}
          </Box>
        </Paper>

        {showResults && (
          <Alert severity={score === currentQuestion.correctAnswers.length ? 'success' : 'warning'} sx={{ mb: 3 }}>
            <Typography variant="h6">
              Sonuç: {score}/{currentQuestion.correctAnswers.length} doğru
            </Typography>
            {currentQuestion.correctAnswers.map((correct, index) => (
              <Typography key={index} variant="body2" sx={{ mt: 1 }}>
                Boşluk {index + 1}: <strong>{correct}</strong>
                {userAnswers[index] !== correct && userAnswers[index] && 
                  ` (Siz: ${userAnswers[index]})`
                }
              </Typography>
            ))}
            <Typography variant="body2" sx={{ mt: 2, fontStyle: 'italic' }}>
              {currentQuestion.explanation}
            </Typography>
          </Alert>
        )}

        <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mt: 4 }}>
          {!isSubmitted && (
            <Button
              variant="contained"
              color="primary"
              size="large"
              onClick={handleSubmit}
              disabled={userAnswers.some(answer => answer === null)}
              sx={{ minWidth: 120 }}
            >
              Kontrol Et
            </Button>
          )}

          <Button
            variant="outlined"
            color="primary"
            size="large"
            onClick={() => navigate('/')}
            sx={{ minWidth: 120 }}
          >
            Ana Sayfa
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default FillInTheBlanksGame;
