import React, { useState, useEffect } from 'react';
import { Box, Typography, Button, TextField, Paper, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';

interface FillInTheBlanksQuestion {
  id: string;
  text: string;
  blanks: {
    position: number;
    correctAnswer: string;
    alternatives: string[];
  }[];
  explanation: string;
}

const FillInTheBlanksGame: React.FC = () => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState<FillInTheBlanksQuestion[]>([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState<string[]>([]);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);

  // Sample data - bu backend'den gelecek
  useEffect(() => {
    const sampleQuestions: FillInTheBlanksQuestion[] = [
      {
        id: 'fill-1',
        text: 'The Spanish are famous for __________ natural sociability and appetite for life. They __________ to put as much energy into enjoying their lives as they do into their work. The __________ lazy Spaniard is a myth known by everybody, but many people fit their work to the demands of the social lives, rather than be ruled by the clock. The day is quite long in Spain, and the Spanish have a word, madrugada, for the time between day and night, __________ the city streets are still full of people enjoying __________.',
        blanks: [
          { position: 0, correctAnswer: 'having', alternatives: ['have', 'to have', 'had', 'being'] },
          { position: 1, correctAnswer: 'are known', alternatives: ['knew', 'have known', 'are knowing', 'will know'] },
          { position: 2, correctAnswer: 'typical', alternatives: ['dominant', 'challenging', 'unpleasant', 'common'] },
          { position: 3, correctAnswer: 'when', alternatives: ['where', 'who', 'which', 'that'] },
          { position: 4, correctAnswer: 'themselves', alternatives: ['theirs', 'their own', 'them', 'each other'] }
        ],
        explanation: 'Bu sorunun doğru cevapları: having (gerund form), are known (passive voice), typical (sıfat), when (zaman bağlacı), themselves (dönüşlü zamir).'
      }
    ];
    setQuestions(sampleQuestions);
    setUserAnswers(Array(sampleQuestions[0]?.blanks.length || 0).fill(''));
  }, []);

  const currentQuestion = questions[currentIndex];

  const handleAnswerChange = (blankIndex: number, value: string) => {
    const newAnswers = [...userAnswers];
    newAnswers[blankIndex] = value;
    setUserAnswers(newAnswers);
  };

  const handleSubmit = () => {
    setIsSubmitted(true);
    let correctCount = 0;
    currentQuestion.blanks.forEach((blank, index) => {
      if (userAnswers[index].toLowerCase().trim() === blank.correctAnswer.toLowerCase()) {
        correctCount++;
      }
    });
    setScore(correctCount);
    setShowResults(true);
  };

  const handleNext = () => {
    if (currentIndex < questions.length - 1) {
      setCurrentIndex(currentIndex + 1);
      setUserAnswers(Array(questions[currentIndex + 1]?.blanks.length || 0).fill(''));
      setIsSubmitted(false);
      setShowResults(false);
    }
  };

  const handleRestart = () => {
    setCurrentIndex(0);
    setUserAnswers(Array(questions[0]?.blanks.length || 0).fill(''));
    setIsSubmitted(false);
    setShowResults(false);
    setScore(0);
  };

  const renderTextWithBlanks = () => {
    if (!currentQuestion) return null;

    const parts = currentQuestion.text.split('__________');
    const result: (string | React.ReactNode)[] = [];

    parts.forEach((part, index) => {
      result.push(part);
      if (index < parts.length - 1) {
        const blankIndex = index;
        const isCorrect = isSubmitted && userAnswers[blankIndex].toLowerCase().trim() === currentQuestion.blanks[blankIndex].correctAnswer.toLowerCase();
        const isIncorrect = isSubmitted && userAnswers[blankIndex].toLowerCase().trim() !== currentQuestion.blanks[blankIndex].correctAnswer.toLowerCase();

        result.push(
          <TextField
            key={`blank-${index}`}
            value={userAnswers[blankIndex]}
            onChange={(e) => handleAnswerChange(blankIndex, e.target.value)}
            variant="outlined"
            size="small"
            disabled={isSubmitted}
            sx={{
              mx: 1,
              minWidth: '120px',
              '& .MuiOutlinedInput-root': {
                backgroundColor: isSubmitted 
                  ? (isCorrect ? '#e8f5e8' : '#ffeaea')
                  : 'white',
                '& fieldset': {
                  borderColor: isSubmitted 
                    ? (isCorrect ? '#4caf50' : '#f44336')
                    : undefined,
                },
              },
            }}
          />
        );
      }
    });

    return result;
  };

  if (!currentQuestion) {
    return (
      <Box sx={{ p: 4, textAlign: 'center' }}>
        <Typography variant="h5">Boşluk Doldurma Oyunu</Typography>
        <Typography>Sorular yükleniyor...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      p: 4 
    }}>
      <Paper sx={{ maxWidth: 900, mx: 'auto', p: 4 }}>
        <Box sx={{ textAlign: 'center', mb: 4 }}>
          <Typography variant="h4" gutterBottom color="primary">
            Boşluk Doldurma Oyunu
          </Typography>
          <Typography variant="h6" color="text.secondary">
            Soru {currentIndex + 1} / {questions.length}
          </Typography>
        </Box>

        <Paper sx={{ p: 3, mb: 3, backgroundColor: '#f8f9fa' }}>
          <Typography variant="body1" sx={{ fontSize: '1.1rem', lineHeight: 1.8 }}>
            {renderTextWithBlanks()}
          </Typography>
        </Paper>

        {showResults && (
          <Alert severity={score === currentQuestion.blanks.length ? 'success' : 'warning'} sx={{ mb: 3 }}>
            <Typography variant="h6">
              Sonuç: {score}/{currentQuestion.blanks.length} doğru
            </Typography>
            {currentQuestion.blanks.map((blank, index) => (
              <Typography key={index} variant="body2" sx={{ mt: 1 }}>
                Boşluk {index + 1}: <strong>{blank.correctAnswer}</strong>
                {userAnswers[index].toLowerCase().trim() !== blank.correctAnswer.toLowerCase() && 
                  ` (Siz: ${userAnswers[index]})`
                }
              </Typography>
            ))}
            <Typography variant="body2" sx={{ mt: 2, fontStyle: 'italic' }}>
              {currentQuestion.explanation}
            </Typography>
          </Alert>
        )}

        <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mt: 4 }}>
          {!isSubmitted && (
            <Button
              variant="contained"
              color="primary"
              size="large"
              onClick={handleSubmit}
              disabled={userAnswers.some(answer => answer.trim() === '')}
              sx={{ minWidth: 120 }}
            >
              Kontrol Et
            </Button>
          )}

          {isSubmitted && currentIndex < questions.length - 1 && (
            <Button
              variant="contained"
              color="success"
              size="large"
              onClick={handleNext}
              sx={{ minWidth: 120 }}
            >
              Sonraki Soru
            </Button>
          )}

          {isSubmitted && currentIndex === questions.length - 1 && (
            <Button
              variant="contained"
              color="secondary"
              size="large"
              onClick={handleRestart}
              sx={{ minWidth: 120 }}
            >
              Tekrar Oyna
            </Button>
          )}

          <Button
            variant="outlined"
            color="primary"
            size="large"
            onClick={() => navigate('/')}
            sx={{ minWidth: 120 }}
          >
            Ana Sayfa
          </Button>
        </Box>
      </Paper>
    </Box>
  );
};

export default FillInTheBlanksGame;
