import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, IconButton, TextField, LinearProgress, CircularProgress } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

interface WordData {
  tr: string;
  en: string;
}

export default function WritingGame() {
  const [index, setIndex] = useState(0);
  const [input, setInput] = useState("");
  const [status, setStatus] = useState<"idle" | "correct" | "wrong">("idle");
  const [score, setScore] = useState(0);
  const [mistakes, setMistakes] = useState(0);
  const [words, setWords] = useState<WordData[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showResult, setShowResult] = useState(false);
  const [time, setTime] = useState(0);
  const [showCorrectAnswer, setShowCorrectAnswer] = useState(false);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  // Fetch questions from API
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        setLoading(true);
        const response = await fetch('http://localhost:4000/api/games/writing/questions');
        if (!response.ok) {
          throw new Error('Failed to fetch questions');
        }
        const data = await response.json();
        setWords(data);
        setError(null);
      } catch (err) {
        console.error('Error fetching questions:', err);
        setError('Sorular yüklenemedi. Lütfen daha sonra tekrar deneyin.');
        // Fallback to hard coded data
        setWords([
          { tr: "masa", en: "table" },
          { tr: "kitap", en: "book" }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, []);
  // Restart game state
  const handleRestart = () => {
    setIndex(0);
    setInput("");
    setStatus("idle");
    setScore(0);
    setMistakes(0);
    setShowResult(false);
    setTime(0);
  };

  useEffect(() => {
    if (showResult) return;
    timerRef.current = setInterval(() => {
      setTime((t) => t + 1);
    }, 1000);
    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, [showResult]);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
    setStatus("idle");
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (input.trim().toLowerCase() === words[index].en) {
      setStatus("correct");
      setScore((s) => s + 1);
      setTimeout(() => {
        setStatus("idle");
        setInput("");
        if (index < words.length - 1) {
          setIndex((i) => i + 1);
        } else {
          setShowResult(true);
          if (timerRef.current) clearInterval(timerRef.current);
        }
      }, 1000);
    } else {
      setStatus("wrong");
      setMistakes((m) => m + 1);
      // Keep status as "wrong" to show feedback
    }
  };

  const handleRetry = () => {
    setStatus("idle");
    setInput("");
  };

  const handleSkip = () => {
    setStatus("idle");
    setInput("");
    if (index < words.length - 1) {
      setIndex((i) => i + 1);
    } else {
      setShowResult(true);
      if (timerRef.current) clearInterval(timerRef.current);
    }
  };

  const handlePrev = () => {
    if (index > 0) {
      setIndex((i) => i - 1);
      setStatus("idle");
      setInput("");
    }
  };

  const handleFinish = () => {
    setShowResult(true);
    if (timerRef.current) clearInterval(timerRef.current);
  };

  const handleCloseResult = () => {
    setShowResult(false);
    setIndex(0);
    setScore(0);
    setMistakes(0);
    setTime(0);
    setInput("");
    setStatus("idle");
  };

  // Loading state
  if (loading) {
    return (
      <Box sx={{ minHeight: "100vh", bgcolor: "#b2ebf2", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", px: 2 }}>
        <CircularProgress size={60} sx={{ color: "#19376D", mb: 2 }} />
        <Typography variant="h6" color="#19376D">Sorular yükleniyor...</Typography>
      </Box>
    );
  }

  // Error state
  if (error) {
    return (
      <Box sx={{ minHeight: "100vh", bgcolor: "#b2ebf2", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", px: 2 }}>
        <Box sx={{ p: 4, maxWidth: 600, width: "100%", textAlign: "center", borderRadius: 4, bgcolor: "#fff" }}>
          <Typography variant="h6" color="error" mb={2}>{error}</Typography>
          <Typography 
            onClick={() => window.location.href = '/questions'}
            sx={{ 
              bgcolor: "#19376D", 
              color: "#fff",
              py: 1.5,
              px: 3,
              borderRadius: 2,
              cursor: "pointer",
              display: "inline-block",
              "&:hover": { bgcolor: "#0f2149" }
            }}
          >
            Geri Dön
          </Typography>
        </Box>
      </Box>
    );
  }

  // No questions available
  if (words.length === 0) {
    return (
      <Box sx={{ minHeight: "100vh", bgcolor: "#b2ebf2", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", px: 2 }}>
        <Box sx={{ p: 4, maxWidth: 600, width: "100%", textAlign: "center", borderRadius: 4, bgcolor: "#fff" }}>
          <Typography variant="h6" color="#19376D" mb={2}>Henüz soru bulunmuyor</Typography>
          <Typography 
            onClick={() => window.location.href = '/questions'}
            sx={{ 
              bgcolor: "#19376D", 
              color: "#fff",
              py: 1.5,
              px: 3,
              borderRadius: 2,
              cursor: "pointer",
              display: "inline-block",
              "&:hover": { bgcolor: "#0f2149" }
            }}
          >
            Geri Dön
          </Typography>
        </Box>
      </Box>
    );
  }

  return (
    <Box sx={{ minHeight: "100vh", bgcolor: "#b2ebf2", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "flex-start", fontFamily: "Inter, Roboto, Open Sans, Arial, sans-serif", pt: { xs: 8, sm: 12 } }}>
      <Box sx={{ width: 400, maxWidth: "95vw", bgcolor: "#19376D", borderRadius: 4, boxShadow: 6, p: 3, mb: 2, color: "#fff", position: "relative" }}>
        {/* Top bar with back button, timer, and gap */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
          <IconButton sx={{ color: "#fff", bgcolor: 'rgba(10,29,86,0.7)', '&:hover': { bgcolor: 'rgba(10,29,86,0.9)' } }} onClick={() => window.location.href = '/questions'}>
            <ArrowBackIcon />
          </IconButton>
          <Box sx={{ flex: 1 }} />
          <Typography sx={{ fontWeight: 600, fontSize: 16, color: '#fff', bgcolor: 'rgba(10,29,86,0.7)', px: 1, borderRadius: 2 }}>{Math.floor(time / 60)}:{(time % 60).toString().padStart(2, '0')}</Typography>
        </Box>
        <LinearProgress
          variant="determinate"
          value={((index + 1) / words.length) * 100}
          sx={{
            height: 8,
            borderRadius: 4,
            mb: 2,
            mt: 1,
            bgcolor: "#fff",
            '& .MuiLinearProgress-bar': {
              backgroundColor: '#1976d2',
              boxShadow: '0 0 8px #1976d2',
              transition: 'background-color 0.2s',
            },
          }}
        />
        <Typography variant="h5" fontWeight={700} align="center" mt={3} mb={2}>Türkçe Kelime: {words[index].tr}</Typography>

        {/* Wrong answer feedback */}
        {status === "wrong" && (
          <Box sx={{ 
            bgcolor: '#ffe6e6', 
            border: '2px solid #e74c3c',
            borderRadius: 2, 
            p: 2, 
            mb: 2,
            textAlign: 'center'
          }}>
            <Typography sx={{ color: '#e74c3c', fontWeight: 600, fontSize: 18, mb: 1 }}>
              ❌ Yanlış yazdınız, tekrar deneyin!
            </Typography>
            <Box sx={{ display: 'flex', gap: 1, justifyContent: 'center', flexWrap: 'wrap' }}>
              <button
                style={{
                  background: '#43ea7c',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 14,
                  border: 'none',
                  borderRadius: 6,
                  padding: '6px 16px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 6px rgba(67,234,124,0.3)',
                }}
                onClick={handleRetry}
              >
                🔄 Tekrar Dene
              </button>
              <button
                style={{
                  background: '#1976d2',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 14,
                  border: 'none',
                  borderRadius: 6,
                  padding: '6px 16px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 6px rgba(25,118,210,0.3)',
                }}
                onClick={() => setShowCorrectAnswer(true)}
              >
                👁️ Doğru Yazımı Göster
              </button>
              <button
                style={{
                  background: '#19376D',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 14,
                  border: 'none',
                  borderRadius: 6,
                  padding: '6px 16px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 6px rgba(25,55,109,0.3)',
                }}
                onClick={handleSkip}
              >
                ⏭️ Pas Geç
              </button>
            </Box>
          </Box>
        )}

        {/* Correct answer feedback */}
        {status === "correct" && (
          <Box sx={{ 
            bgcolor: '#e6ffe6', 
            border: '2px solid #43ea7c',
            borderRadius: 2, 
            p: 2, 
            mb: 2,
            textAlign: 'center'
          }}>
            <Typography sx={{ color: '#43ea7c', fontWeight: 600, fontSize: 18 }}>
              ✅ Doğru yazdınız! Tebrikler!
            </Typography>
          </Box>
        )}

        <form onSubmit={handleSubmit}>
          <TextField
            value={input}
            onChange={handleChange}
            placeholder="İngilizcesini yazın"
            variant="outlined"
            fullWidth
            sx={{
              minWidth: 300,
              maxWidth: 600,
              fontSize: 20,
              bgcolor: "#fff",
              borderRadius: 2,
              input: { textAlign: "center", fontSize: 22, fontWeight: 600 },
              "& .MuiOutlinedInput-root": {
                "& fieldset": {
                  borderColor: status === "correct" ? "#43ea7c" : status === "wrong" ? "#e74c3c" : "#19376D",
                  boxShadow: status === "correct" ? "0 0 8px #43ea7c88" : status === "wrong" ? "0 0 8px #e74c3c88" : undefined,
                  borderWidth: status === "correct" || status === "wrong" ? 3 : 1,
                  transition: "border-color 0.3s, box-shadow 0.3s, border-width 0.3s",
                },
              },
            }}
          />
        </form>
        {/* Navigation buttons - Hide during wrong state */}
        {!showResult && status !== "wrong" && (
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3, gap: 2 }}>
            <button
              style={{
                background: '#fff',
                color: '#19376D',
                fontWeight: 700,
                fontSize: 16,
                border: '2px solid #19376D',
                borderRadius: 8,
                padding: '8px 20px',
                cursor: index === 0 ? 'not-allowed' : 'pointer',
                opacity: index === 0 ? 0.5 : 1,
                minWidth: 100,
              }}
              onClick={handlePrev}
              disabled={index === 0}
            >
              Geri
            </button>
            <button
              style={{
                background: '#fff',
                color: '#19376D',
                fontWeight: 700,
                fontSize: 16,
                border: '2px solid #19376D',
                borderRadius: 8,
                padding: '8px 20px',
                cursor: 'pointer',
                minWidth: 100,
              }}
              onClick={handleSkip}
            >
              Pas Geç
            </button>
          </Box>
        )}
        {/* Bitir button, only show if not finished and not in wrong state */}
        {!showResult && status !== "wrong" && (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <button
              style={{
                background: 'linear-gradient(90deg,#43ea7c,#19376D)',
                color: '#fff',
                fontWeight: 700,
                fontSize: 18,
                border: 'none',
                borderRadius: 8,
                padding: '12px 32px',
                cursor: 'pointer',
                boxShadow: '0 2px 8px rgba(10,29,86,0.15)',
                transition: 'background 0.3s',
              }}
              onClick={handleFinish}
            >
              Bitir
            </button>
          </Box>
        )}
      </Box>
      {/* Result popup bubble */}
      {showResult && (
        <Box sx={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', bgcolor: 'rgba(10,29,86,0.7)', zIndex: 9999, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Box sx={{ bgcolor: '#fff', borderRadius: 6, boxShadow: 8, p: 4, minWidth: 320, maxWidth: '90vw', textAlign: 'center', position: 'relative' }}>
            <Typography variant="h5" fontWeight={700} color="#19376D" mb={2}>Oyun Sonucu</Typography>
            <Typography fontSize={20} fontWeight={600} color="#43ea7c" mb={1}>Doğru: {score}</Typography>
            <Typography fontSize={20} fontWeight={600} color="#e74c3c" mb={1}>Yanlış: {mistakes}</Typography>
            <Typography fontSize={18} fontWeight={500} color="#19376D" mb={2}>Süre: {Math.floor(time / 60)}:{(time % 60).toString().padStart(2, '0')}</Typography>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mt: 2 }}>
              <button
                style={{
                  background: 'linear-gradient(90deg,#19376D,#43ea7c)',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 16,
                  border: 'none',
                  borderRadius: 8,
                  padding: '10px 28px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(10,29,86,0.15)',
                }}
                onClick={() => window.location.href = '/questions'}
              >
                Kapat
              </button>
              <button
                style={{
                  background: 'linear-gradient(90deg,#43ea7c,#19376D)',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 16,
                  border: 'none',
                  borderRadius: 8,
                  padding: '10px 28px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(10,29,86,0.15)',
                }}
                onClick={handleRestart}
              >
                Yeniden Başlat
              </button>
            </Box>
          </Box>
        </Box>
      )}

      {/* Correct Answer Popup */}
      {showCorrectAnswer && (
        <Box sx={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', bgcolor: 'rgba(10,29,86,0.7)', zIndex: 9999, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Box sx={{ bgcolor: '#fff', borderRadius: 6, boxShadow: 8, p: 4, minWidth: 320, maxWidth: '90vw', textAlign: 'center', position: 'relative' }}>
            <Typography variant="h5" fontWeight={700} color="#19376D" mb={2}>Doğru Yazım</Typography>
            <Typography fontSize={18} fontWeight={500} color="#666" mb={2}>
              <strong>{words[index].tr}</strong> kelimesinin İngilizce karşılığı:
            </Typography>
            <Box sx={{ 
              bgcolor: '#e3f2fd', 
              border: '2px solid #1976d2',
              borderRadius: 3, 
              p: 3, 
              mb: 3
            }}>
              <Typography sx={{ 
                color: '#1976d2', 
                fontWeight: 700, 
                fontSize: 28,
                fontFamily: 'monospace',
                letterSpacing: 2
              }}>
                {words[index].en}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center' }}>
              <button
                style={{
                  background: '#19376D',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 16,
                  border: 'none',
                  borderRadius: 8,
                  padding: '10px 24px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(25,55,109,0.3)',
                }}
                onClick={() => setShowCorrectAnswer(false)}
              >
                ✖️ Kapat
              </button>
            </Box>
          </Box>
        </Box>
      )}
    </Box>
  );
}
