import React, { useState, useEffect, useRef } from "react";
import { Box, Typography, IconButton, LinearProgress } from "@mui/material";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";

const words = [
  { tr: "okul", en: ["school", "hospital"], correct: "school", alternatives: ["university", "library", "office"] },
  { tr: "masa", en: ["table", "chair"], correct: "table", alternatives: ["desk", "shelf", "window"] },
  { tr: "kitap", en: ["book", "pen"], correct: "book", alternatives: ["magazine", "newspaper", "notebook"] },
  { tr: "ev", en: ["house", "car"], correct: "house", alternatives: ["building", "apartment", "hotel"] },
  { tr: "araba", en: ["car", "bus"], correct: "car", alternatives: ["bicycle", "motorcycle", "truck"] },
];

// Helper function to get alternative wrong option
const getAlternativeOption = (wordData: any, originalWrongOption: string) => {
  const availableAlternatives = wordData.alternatives.filter((alt: string) => alt !== wordData.correct);
  return availableAlternatives[Math.floor(Math.random() * availableAlternatives.length)] || originalWrongOption;
};

const gradients = [
  "linear-gradient(135deg, #0A1D56 0%, #1976d2 100%)",
  "linear-gradient(135deg, #0A1D56 0%, #1976d2 100%)",
];

export default function WordHuntGame() {
  const [index, setIndex] = useState(0);
  const [selected, setSelected] = useState<string | null>(null);
  const [status, setStatus] = useState<"idle" | "correct" | "wrong">("idle");
  const [score, setScore] = useState(0);
  const [mistakes, setMistakes] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [time, setTime] = useState(0);
  const [currentOptions, setCurrentOptions] = useState<string[]>([]);
  const [wrongAttempts, setWrongAttempts] = useState(0);

  // Initialize options for current question
  useEffect(() => {
    if (words[index]) {
      setCurrentOptions([...words[index].en]);
      setWrongAttempts(0);
    }
  }, [index]);

  // Restart game state
  const handleRestart = () => {
    setIndex(0);
    setSelected(null);
    setStatus("idle");
    setScore(0);
    setMistakes(0);
    setShowResult(false);
    setTime(0);
    setCurrentOptions([...words[0].en]);
    setWrongAttempts(0);
  };
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    if (showResult) return;
    timerRef.current = setInterval(() => {
      setTime((t) => t + 1);
    }, 1000);
    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, [showResult]);

  const handleSelect = (option: string) => {
    if (status !== "idle") return;
    setSelected(option);
    if (option === words[index].correct) {
      setStatus("correct");
      setScore((s) => s + 1);
      setTimeout(() => {
        setStatus("idle");
        setSelected(null);
        if (index < words.length - 1) {
          setIndex((i) => i + 1);
        } else {
          setShowResult(true);
          if (timerRef.current) clearInterval(timerRef.current);
        }
      }, 1000);
    } else {
      setStatus("wrong");
      setMistakes((m) => m + 1);
      setWrongAttempts(prev => prev + 1);

      // Show wrong card for 2 seconds, then automatically change the wrong option
      setTimeout(() => {
        setStatus("idle");
        setSelected(null);

        // Change the wrong option to give user a better chance
        const wrongOption = option;
        const correctOption = words[index].correct;
        const newWrongOption = getAlternativeOption(words[index], wrongOption);

        // Update current options with new wrong option
        setCurrentOptions([correctOption, newWrongOption]);
      }, 2000);
    }
  };

  const handleSkip = () => {
    setStatus("idle");
    setSelected(null);
    setWrongAttempts(0);
    if (index < words.length - 1) {
      setIndex((i) => i + 1);
    } else {
      // Only finish if user skips on last question
      setShowResult(true);
      if (timerRef.current) clearInterval(timerRef.current);
    }
  };

  const handlePrev = () => {
    if (index > 0) {
      setIndex((i) => i - 1);
      setStatus("idle");
      setSelected(null);
      setWrongAttempts(0);
    }
  };

  const handleFinish = () => {
    setShowResult(true);
    if (timerRef.current) clearInterval(timerRef.current);
  };

  const handleCloseResult = () => {
    setShowResult(false);
    setIndex(0);
    setScore(0);
    setMistakes(0);
    setTime(0);
    setCurrentOptions([...words[0].en]);
    setWrongAttempts(0);
  };

  return (
    <Box sx={{ minHeight: "100vh", bgcolor: "#b2ebf2", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "flex-start", fontFamily: "Inter, Roboto, Open Sans, Arial, sans-serif", px: 2, pt: { xs: 8, sm: 12 } }}>
      <Box sx={{ width: '100%', maxWidth: 400, bgcolor: "#19376D", borderRadius: 4, boxShadow: 6, p: 3, mb: 2, color: "#fff", position: "relative", mx: 'auto', overflow: 'hidden' }}>
        {/* Back button, fixed to top left, above content */}
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
          <IconButton sx={{ color: "#fff", bgcolor: 'rgba(10,29,86,0.7)', '&:hover': { bgcolor: 'rgba(10,29,86,0.9)' } }} onClick={() => window.location.href = '/questions'}>
            <ArrowBackIcon />
          </IconButton>
          <Box sx={{ flex: 1 }} />
          <Typography sx={{ fontWeight: 600, fontSize: 16, color: '#fff', bgcolor: 'rgba(10,29,86,0.7)', px: 1, borderRadius: 2 }}>{Math.floor(time / 60)}:{(time % 60).toString().padStart(2, '0')}</Typography>
        </Box>
        <LinearProgress
          variant="determinate"
          value={((index + 1) / words.length) * 100}
          sx={{
            height: 8,
            borderRadius: 4,
            mb: 2,
            mt: 1,
            bgcolor: "#fff",
            '& .MuiLinearProgress-bar': {
              backgroundColor: '#1976d2',
              boxShadow: '0 0 8px #1976d2',
              transition: 'background-color 0.2s',
            },
          }}
        />
        <Typography variant="h5" fontWeight={700} align="center" mt={3} mb={2}>Türkçe Kelime: {words[index].tr}</Typography>

        {/* Wrong answer feedback - shows for 2 seconds then auto-disappears */}
        {status === "wrong" && (
          <Box sx={{ 
            bgcolor: '#ffe6e6', 
            border: '2px solid #e74c3c',
            borderRadius: 2, 
            p: 2, 
            mb: 2,
            textAlign: 'center',
            animation: 'fadeIn 0.3s ease-in'
          }}>
            <Typography sx={{ color: '#e74c3c', fontWeight: 600, fontSize: 18, mb: 1 }}>
              ❌ Yanlış cevap!
            </Typography>
            <Typography sx={{ color: '#666', fontSize: 14 }}>
              Seçenekler değiştiriliyor...
            </Typography>
          </Box>
        )}

        {/* Correct answer feedback */}
        {status === "correct" && (
          <Box sx={{ 
            bgcolor: '#e6ffe6', 
            border: '2px solid #43ea7c',
            borderRadius: 2, 
            p: 2, 
            mb: 2,
            textAlign: 'center'
          }}>
            <Typography sx={{ color: '#43ea7c', fontWeight: 600, fontSize: 18 }}>
              ✅ Doğru Cevap! Tebrikler!
            </Typography>
          </Box>
        )}

        <Box sx={{ display: "flex", flexDirection: { xs: 'column', sm: 'row' }, gap: 2, justifyContent: "center", alignItems: 'center', mt: 3 }}>
          {currentOptions.map((option, i) => (
            <Box
              key={option}
              sx={{
                width: { xs: '100%', sm: 200 },
                minWidth: 160,
                maxWidth: 240,
                height: 120,
                borderRadius: 3,
                // boxShadow: 2, // removed to avoid duplicate property
                bgcolor: "#fff",
                background: gradients[i % gradients.length],
                color: "#fff",
                fontSize: 28,
                fontWeight: 700,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                cursor: "pointer",
                position: "relative",
                transition: "all 0.4s cubic-bezier(.4,2,.3,1)",
                mb: { xs: 2, sm: 0 },
                border: selected === option && status === "correct" ? '4px solid #43ea7c' : selected === option && status === "wrong" ? '4px solid #e74c3c' : '4px solid transparent',
                boxShadow: selected === option && status === "correct" ? '0 0 12px #43ea7c88' : selected === option && status === "wrong" ? '0 0 12px #e74c3c88' : '0 2px 8px #0001',
                transform: selected === option && (status === "correct" || status === "wrong") ? 'scale(1.05)' : undefined,
              }}
              onClick={() => status === "idle" ? handleSelect(option) : undefined}
              style={{ 
                pointerEvents: status === "correct" ? 'none' : 'auto',
                opacity: status === "wrong" && selected === option ? 0.7 : 1
              }}
            >
              {option}
            </Box>
          ))}
        </Box>
        {/* Navigation buttons - Only show when not in wrong state */}
        {!showResult && status !== "wrong" && (
          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3, gap: 2 }}>
            <button
              style={{
                background: '#fff',
                color: '#19376D',
                fontWeight: 700,
                fontSize: 16,
                border: '2px solid #19376D',
                borderRadius: 8,
                padding: '8px 20px',
                cursor: index === 0 ? 'not-allowed' : 'pointer',
                opacity: index === 0 ? 0.5 : 1,
                minWidth: 100,
              }}
              onClick={handlePrev}
              disabled={index === 0}
            >
              Geri
            </button>
            <button
              style={{
                background: '#fff',
                color: '#19376D',
                fontWeight: 700,
                fontSize: 16,
                border: '2px solid #19376D',
                borderRadius: 8,
                padding: '8px 20px',
                cursor: 'pointer',
                minWidth: 100,
              }}
              onClick={handleSkip}
            >
              Pas Geç
            </button>
          </Box>
        )}
        {/* Bitir button, only show if not finished and not in wrong state */}
        {!showResult && status !== "wrong" && (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <button
              style={{
                background: 'linear-gradient(90deg,#43ea7c,#19376D)',
                color: '#fff',
                fontWeight: 700,
                fontSize: 18,
                border: 'none',
                borderRadius: 8,
                padding: '12px 32px',
                cursor: 'pointer',
                boxShadow: '0 2px 8px rgba(10,29,86,0.15)',
                transition: 'background 0.3s',
              }}
              onClick={handleFinish}
            >
              Bitir
            </button>
          </Box>
        )}
      </Box>
      {/* Result popup bubble */}
      {showResult && (
        <Box sx={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', bgcolor: 'rgba(10,29,86,0.7)', zIndex: 9999, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Box sx={{ bgcolor: '#fff', borderRadius: 6, boxShadow: 8, p: 4, minWidth: 320, maxWidth: '90vw', textAlign: 'center', position: 'relative' }}>
            <Typography variant="h5" fontWeight={700} color="#19376D" mb={2}>Oyun Sonucu</Typography>
            <Typography fontSize={20} fontWeight={600} color="#43ea7c" mb={1}>Doğru: {score}</Typography>
            <Typography fontSize={20} fontWeight={600} color="#e74c3c" mb={1}>Yanlış: {mistakes}</Typography>
            <Typography fontSize={18} fontWeight={500} color="#19376D" mb={2}>Süre: {Math.floor(time / 60)}:{(time % 60).toString().padStart(2, '0')}</Typography>
            <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mt: 2 }}>
              <button
                style={{
                  background: 'linear-gradient(90deg,#19376D,#43ea7c)',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 16,
                  border: 'none',
                  borderRadius: 8,
                  padding: '10px 28px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(10,29,86,0.15)',
                }}
                onClick={() => window.location.href = '/questions'}
              >
                Kapat
              </button>
              <button
                style={{
                  background: 'linear-gradient(90deg,#43ea7c,#19376D)',
                  color: '#fff',
                  fontWeight: 700,
                  fontSize: 16,
                  border: 'none',
                  borderRadius: 8,
                  padding: '10px 28px',
                  cursor: 'pointer',
                  boxShadow: '0 2px 8px rgba(10,29,86,0.15)',
                }}
                onClick={handleRestart}
              >
                Yeniden Başlat
              </button>
            </Box>
          </Box>
        </Box>
      )}
    </Box>
  );
}
