import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  LinearProgress,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Alert,
  Fade,
  Card,
  CardContent,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import RefreshIcon from '@mui/icons-material/Refresh';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import { useNavigate } from 'react-router-dom';

interface WordPair {
  id: number;
  turkish: string;
  english: string;
  isMatched: boolean;
}

interface SelectedCard {
  id: number;
  type: 'turkish' | 'english';
  word: string;
}

const A1_WORDS: WordPair[] = [
  { id: 1, turkish: 'merhaba', english: 'hello', isMatched: false },
  { id: 2, turkish: 'dünya', english: 'world', isMatched: false },
  { id: 3, turkish: 'aile', english: 'family', isMatched: false },
  { id: 4, turkish: 'okul', english: 'school', isMatched: false },
  { id: 5, turkish: 'kitap', english: 'book', isMatched: false },
  { id: 6, turkish: 'su', english: 'water', isMatched: false },
  { id: 7, turkish: 'ev', english: 'house', isMatched: false },
  { id: 8, turkish: 'araba', english: 'car', isMatched: false },
  { id: 9, turkish: 'yemek', english: 'food', isMatched: false },
  { id: 10, turkish: 'zaman', english: 'time', isMatched: false },
  { id: 11, turkish: 'para', english: 'money', isMatched: false },
  { id: 12, turkish: 'çalışma', english: 'work', isMatched: false },
  { id: 13, turkish: 'oyun', english: 'game', isMatched: false },
  { id: 14, turkish: 'müzik', english: 'music', isMatched: false },
  { id: 15, turkish: 'hayat', english: 'life', isMatched: false },
];

const LEVELS = [
  { value: 'A1', label: 'A1 - Başlangıç' },
  { value: 'A2', label: 'A2 - Temel' },
  { value: 'B1', label: 'B1 - Orta Alt' },
  { value: 'B2', label: 'B2 - Orta Üst' },
];

export default function WordMatchingGame() {
  const navigate = useNavigate();
  const [words, setWords] = useState<WordPair[]>([]);
  const [selectedLevel, setSelectedLevel] = useState<string>('A1');
  const [showLevelDialog, setShowLevelDialog] = useState<boolean>(true);
  const [selectedCards, setSelectedCards] = useState<SelectedCard[]>([]);
  const [matchedPairs, setMatchedPairs] = useState<number>(0);
  const [gameCompleted, setGameCompleted] = useState<boolean>(false);
  const [timer, setTimer] = useState<number>(0);
  const [isPlaying, setIsPlaying] = useState<boolean>(false);
  const [wrongMatch, setWrongMatch] = useState<boolean>(false);
  const [correctMatch, setCorrectMatch] = useState<boolean>(false);

  // Timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isPlaying && !gameCompleted) {
      interval = setInterval(() => {
        setTimer(prev => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isPlaying, gameCompleted]);

  // Format timer
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Shuffle array
  const shuffleArray = (array: any[]) => {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  };

  const startGame = () => {
    const levelWords = selectedLevel === 'A1' ? A1_WORDS : A1_WORDS; // For now, using A1 words for all levels
    setWords(levelWords.map(word => ({ ...word, isMatched: false })));
    setMatchedPairs(0);
    setGameCompleted(false);
    setSelectedCards([]);
    setTimer(0);
    setIsPlaying(true);
    setShowLevelDialog(false);
  };

  const handleCardClick = (id: number, type: 'turkish' | 'english', word: string) => {
    if (selectedCards.length === 2) return;

    const newCard: SelectedCard = { id, type, word };
    const newSelectedCards = [...selectedCards, newCard];
    setSelectedCards(newSelectedCards);

    if (newSelectedCards.length === 2) {
      checkMatch(newSelectedCards);
    }
  };

  const checkMatch = (cards: SelectedCard[]) => {
    const [card1, card2] = cards;

    // Check if both cards are from the same pair but different types
    const word1 = words.find(w => w.id === card1.id);
    const word2 = words.find(w => w.id === card2.id);

    if (word1 && word2 && card1.id === card2.id && card1.type !== card2.type) {
      // Correct match
      setWords(prev => prev.map(word => 
        word.id === card1.id ? { ...word, isMatched: true } : word
      ));
      setMatchedPairs(prev => prev + 1);
      setCorrectMatch(true);

      setTimeout(() => {
        setCorrectMatch(false);
        setSelectedCards([]);

        // Check if game is completed
        if (matchedPairs + 1 === words.length) {
          setGameCompleted(true);
          setIsPlaying(false);
        }
      }, 1000);
    } else {
      // Wrong match
      setWrongMatch(true);
      setTimeout(() => {
        setWrongMatch(false);
        setSelectedCards([]);
      }, 1000);
    }
  };

  const resetGame = () => {
    setWords(words.map(word => ({ ...word, isMatched: false })));
    setMatchedPairs(0);
    setGameCompleted(false);
    setSelectedCards([]);
    setTimer(0);
    setIsPlaying(true);
  };

  const isCardSelected = (id: number, type: 'turkish' | 'english') => {
    return selectedCards.some(card => card.id === id && card.type === type);
  };

  const isCardMatched = (id: number) => {
    return words.find(word => word.id === id)?.isMatched || false;
  };

  // Create shuffled card arrays
  const shuffledTurkishCards = shuffleArray(words.map(word => ({ ...word, type: 'turkish' as const })));
  const shuffledEnglishCards = shuffleArray(words.map(word => ({ ...word, type: 'english' as const })));

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: '#b2dfdb',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        pb: { xs: 12, md: 16 },
        pt: 0,
        px: 2,
      }}
    >
      {/* Level Selection Dialog */}
      <Dialog 
        open={showLevelDialog} 
        onClose={() => {}}
        maxWidth="sm" 
        fullWidth
        PaperProps={{
          sx: {
            borderRadius: 3,
            background: 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.9) 100%)',
          }
        }}
      >
        <DialogTitle sx={{ textAlign: 'center', color: '#00695c', fontWeight: 700 }}>
          Seviye Seçin
        </DialogTitle>
        <DialogContent>
          <FormControl fullWidth sx={{ mt: 2 }}>
            <InputLabel>Seviye</InputLabel>
            <Select
              value={selectedLevel}
              label="Seviye"
              onChange={(e) => setSelectedLevel(e.target.value)}
            >
              {LEVELS.map((level) => (
                <MenuItem key={level.value} value={level.value}>
                  {level.label}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <Typography variant="body2" color="text.secondary" sx={{ mt: 2, textAlign: 'center' }}>
            15 kelime çifti eşleştirerek oyunu tamamlayın!
          </Typography>
        </DialogContent>
        <DialogActions sx={{ justifyContent: 'center', pb: 3 }}>
          <Button
            variant="contained"
            onClick={startGame}
            sx={{
              background: 'linear-gradient(90deg, #00b894 0%, #00cec9 100%)',
              color: '#fff',
              fontWeight: 700,
              borderRadius: 2,
              px: 4,
              py: 1.5,
              '&:hover': {
                background: 'linear-gradient(90deg, #00cec9 0%, #00b894 100%)',
              },
            }}
          >
            Oyunu Başlat
          </Button>
        </DialogActions>
      </Dialog>

      <Paper
        elevation={6}
        sx={{
          p: 0,
          borderRadius: 4,
          minWidth: 340,
          width: '100%',
          maxWidth: 1200,
          mt: { xs: 1, md: '15px' },
          background: 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.9) 100%)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
        }}
      >
        {/* Header */}
        <Box
          sx={{
            background: 'linear-gradient(135deg, #FF6B35 0%, #F7931E 100%)',
            color: '#fff',
            p: { xs: 3, md: 4 },
            borderTopLeftRadius: 'inherit',
            borderTopRightRadius: 'inherit',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
          }}
        >
          <Button
            startIcon={<ArrowBackIcon />}
            onClick={() => navigate('/questions')}
            sx={{ color: '#fff', fontWeight: 600 }}
          >
            Geri
          </Button>

          <Box sx={{ textAlign: 'center', flex: 1 }}>
            <Typography variant="h5" fontWeight={700} mb={1}>
              Kelime Eşleştirme
            </Typography>
            <Typography variant="body2" sx={{ opacity: 0.9 }}>
              Seviye: {selectedLevel} • Süre: {formatTime(timer)}
            </Typography>
          </Box>

          <Button
            startIcon={<RefreshIcon />}
            onClick={resetGame}
            sx={{ color: '#fff', fontWeight: 600 }}
            disabled={!isPlaying && !gameCompleted}
          >
            Yenile
          </Button>
        </Box>

        {/* Progress */}
        <Box sx={{ p: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6" color="#00695c" fontWeight={600}>
              İlerleme: {matchedPairs}/15
            </Typography>
            <Chip 
              label={`${Math.round((matchedPairs / 15) * 100)}%`}
              color="primary"
              sx={{ fontWeight: 600 }}
            />
          </Box>
          <LinearProgress 
            variant="determinate" 
            value={(matchedPairs / 15) * 100}
            sx={{ 
              height: 8, 
              borderRadius: 4,
              backgroundColor: '#e0f2f1',
              '& .MuiLinearProgress-bar': {
                background: 'linear-gradient(90deg, #FF6B35 0%, #F7931E 100%)',
                borderRadius: 4,
              }
            }}
          />
        </Box>

        {/* Game Area */}
        <Box sx={{ p: 3, pt: 0 }}>
          {/* Status Messages */}
          {wrongMatch && (
            <Fade in={wrongMatch}>
              <Alert 
                severity="error" 
                icon={<ErrorIcon />}
                sx={{ mb: 2, borderRadius: 2 }}
              >
                Yanlış eşleşme! Tekrar deneyin.
              </Alert>
            </Fade>
          )}

          {correctMatch && (
            <Fade in={correctMatch}>
              <Alert 
                severity="success" 
                icon={<CheckCircleIcon />}
                sx={{ mb: 2, borderRadius: 2 }}
              >
                Harika! Doğru eşleşme!
              </Alert>
            </Fade>
          )}

          {gameCompleted && (
            <Alert 
              severity="success" 
              icon={<CheckCircleIcon />}
              sx={{ mb: 3, borderRadius: 2, fontSize: 16 }}
            >
              <strong>Tebrikler!</strong> Oyunu {formatTime(timer)} sürede tamamladınız!
            </Alert>
          )}

          {/* Cards Grid */}
          <Box sx={{ 
            display: 'grid', 
            gridTemplateColumns: { xs: '1fr 1fr', md: '1fr 1fr' },
            gap: 3,
            mb: 3
          }}>
            {/* Turkish Cards Column */}
            <Box>
              <Typography variant="h6" color="#00695c" mb={2} textAlign="center" fontWeight={600}>
                Türkçe
              </Typography>
              <Box sx={{ display: 'grid', gap: 1.5 }}>
                {shuffledTurkishCards.map((word) => (
                  <Button
                    key={`turkish-${word.id}`}
                    variant="contained"
                    onClick={() => handleCardClick(word.id, 'turkish', word.turkish)}
                    disabled={isCardMatched(word.id) || selectedCards.length === 2}
                    sx={{
                      minHeight: 50,
                      borderRadius: 2,
                      textTransform: 'none',
                      fontSize: 16,
                      fontWeight: 600,
                      background: isCardMatched(word.id) 
                        ? 'linear-gradient(90deg, #4CAF50 0%, #66BB6A 100%)'
                        : isCardSelected(word.id, 'turkish')
                        ? 'linear-gradient(90deg, #FF6B35 0%, #F7931E 100%)'
                        : 'linear-gradient(90deg, #2196F3 0%, #42A5F5 100%)',
                      color: '#fff',
                      border: isCardSelected(word.id, 'turkish') ? '3px solid #FF8A65' : 'none',
                      '&:hover': {
                        background: isCardMatched(word.id) 
                          ? 'linear-gradient(90deg, #4CAF50 0%, #66BB6A 100%)'
                          : 'linear-gradient(90deg, #1976D2 0%, #2196F3 100%)',
                        transform: isCardMatched(word.id) ? 'none' : 'scale(1.02)',
                      },
                      '&:disabled': {
                        opacity: isCardMatched(word.id) ? 1 : 0.6,
                      },
                    }}
                  >
                    {word.turkish}
                  </Button>
                ))}
              </Box>
            </Box>

            {/* English Cards Column */}
            <Box>
              <Typography variant="h6" color="#00695c" mb={2} textAlign="center" fontWeight={600}>
                İngilizce
              </Typography>
              <Box sx={{ display: 'grid', gap: 1.5 }}>
                {shuffledEnglishCards.map((word) => (
                  <Button
                    key={`english-${word.id}`}
                    variant="contained"
                    onClick={() => handleCardClick(word.id, 'english', word.english)}
                    disabled={isCardMatched(word.id) || selectedCards.length === 2}
                    sx={{
                      minHeight: 50,
                      borderRadius: 2,
                      textTransform: 'none',
                      fontSize: 16,
                      fontWeight: 600,
                      background: isCardMatched(word.id) 
                        ? 'linear-gradient(90deg, #4CAF50 0%, #66BB6A 100%)'
                        : isCardSelected(word.id, 'english')
                        ? 'linear-gradient(90deg, #FF6B35 0%, #F7931E 100%)'
                        : 'linear-gradient(90deg, #9C27B0 0%, #BA68C8 100%)',
                      color: '#fff',
                      border: isCardSelected(word.id, 'english') ? '3px solid #FF8A65' : 'none',
                      '&:hover': {
                        background: isCardMatched(word.id) 
                          ? 'linear-gradient(90deg, #4CAF50 0%, #66BB6A 100%)'
                          : 'linear-gradient(90deg, #7B1FA2 0%, #9C27B0 100%)',
                        transform: isCardMatched(word.id) ? 'none' : 'scale(1.02)',
                      },
                      '&:disabled': {
                        opacity: isCardMatched(word.id) ? 1 : 0.6,
                      },
                    }}
                  >
                    {word.english}
                  </Button>
                ))}
              </Box>
            </Box>
          </Box>

          {/* Game Actions */}
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 3 }}>
            <Button
              variant="outlined"
              onClick={() => setShowLevelDialog(true)}
              sx={{
                borderColor: '#FF6B35',
                color: '#FF6B35',
                fontWeight: 600,
                borderRadius: 2,
                px: 3,
                '&:hover': {
                  borderColor: '#F7931E',
                  backgroundColor: 'rgba(255, 107, 53, 0.04)',
                },
              }}
            >
              Seviye Değiştir
            </Button>

            {gameCompleted && (
              <Button
                variant="contained"
                onClick={startGame}
                sx={{
                  background: 'linear-gradient(90deg, #FF6B35 0%, #F7931E 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  borderRadius: 2,
                  px: 3,
                  '&:hover': {
                    background: 'linear-gradient(90deg, #F7931E 0%, #FF6B35 100%)',
                  },
                }}
              >
                Tekrar Oyna
              </Button>
            )}
          </Box>
        </Box>
      </Paper>
    </Box>
  );
}
