import React, { useEffect, useState, useRef } from 'react';
import { useParams, useLocation, useNavigate } from 'react-router-dom';
import { Box, Paper, Typography, Button, CircularProgress, IconButton } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

interface Question {
  id: string;
  text: string;
  options: string[];
  correct: string;
  explanation: string;
  categoryId: number;
  seriesId: string;
}

const Exam: React.FC = () => {
  const { testId } = useParams();
  const location = useLocation();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState<Question[]>(location.state?.questions || []);
  const [loading, setLoading] = useState(location.state?.questions ? false : true);
  const [current, setCurrent] = useState(0);
  const [selected, setSelected] = useState<string | null>(null);
  const [showResult, setShowResult] = useState(false);
  const [showExplanation, setShowExplanation] = useState(false);
  const [score, setScore] = useState(0);
  const [mistakes, setMistakes] = useState(0);
  const [showSummary, setShowSummary] = useState(false);
  const [time, setTime] = useState(0);
  const [reviewMode, setReviewMode] = useState(false);
  const [answers, setAnswers] = useState<(string | null)[]>([]);
  const [timerPaused, setTimerPaused] = useState(false);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  useEffect(() => {
    // Only fetch if not coming from state
    if (!location.state?.questions) {
      setLoading(true);
      fetch(`http://localhost:4000/api/tests/${testId}/questions`)
        .then(res => res.json())
        .then(data => {
          setQuestions(data.questions || []);
          setLoading(false);
        });
    }
  }, [testId, location.state]);

  // Initialize answers array when questions are loaded
  useEffect(() => {
    if (questions.length > 0 && answers.length !== questions.length) {
      setAnswers(Array(questions.length).fill(null));
    }
  }, [questions, answers.length]);

  // Timer logic - pause when answer is selected or exam is finished
  useEffect(() => {
    if (showSummary || timerPaused) return;
    timerRef.current = setInterval(() => {
      setTime((t) => t + 1);
    }, 1000);
    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, [showSummary, timerPaused]);

  if (loading) {
    return (
      <Box sx={{ minHeight: '100vh', bgcolor: '#f7fafd', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <CircularProgress />
      </Box>
    );
  }

  if (!questions.length) {
    return <Typography align="center" mt={8}>No questions found for this exam.</Typography>;
  }

  const q = questions[current];
  // Ensure options is always an array
  let options: string[] = [];
  if (Array.isArray(q.options)) {
    options = q.options;
  } else if (typeof q.options === 'string') {
    try {
      options = JSON.parse(q.options);
    } catch {
      options = [];
    }
  }

  // Helper function to get correct answer text
  const getCorrectAnswerText = () => {
    if (!q.correct || options.length === 0) return q.correct;
    const correctIndex = q.correct.charCodeAt(0) - 65; // A=0, B=1, C=2, D=3
    return options[correctIndex] || q.correct;
  };

  const handleSelect = (optionIndex: number) => {
    const selectedLetter = String.fromCharCode(65 + optionIndex); // 0->A, 1->B, 2->C, 3->D
    setSelected(selectedLetter);
    setShowResult(true);
    setTimerPaused(true); // Pause timer when answer is selected
    setAnswers((prev) => {
      const copy = [...prev];
      copy[current] = selectedLetter;
      return copy;
    });
    if (selectedLetter === q.correct) {
      setScore((s) => s + 1);
    } else {
      setMistakes((m) => m + 1);
    }
  };

  const nextQuestion = () => {
    if (current < questions.length - 1) {
      setCurrent(c => c + 1);
      setSelected(answers[current + 1] || null);
      setShowResult(!!answers[current + 1]);
      setShowExplanation(false);
      setTimerPaused(false); // Resume timer when moving to next question
    }
  };

  // Sınavı history'ye kaydet
  const saveToHistory = async () => {
    const token = localStorage.getItem('token');
    try {
      await fetch('http://localhost:4000/api/history', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          ...(token ? { 'Authorization': `Bearer ${token}` } : {})
        },
        body: JSON.stringify({
          category: typeof questions[0]?.categoryId === 'string' ? questions[0]?.categoryId : String(questions[0]?.categoryId),
          seriesId: questions[0]?.seriesId || null,
          duration: time,
          correct: score,
          incorrect: mistakes,
          questions: questions.map((q, idx) => ({
            questionId: q.id,
            userAnswer: answers[idx],
            isCorrect: answers[idx] === q.correct
          }))
        })
      });
    } catch (e) {}
  };

  const handleFinish = async () => {
    // Ensure answers array is properly sized
    if (answers.length === 0 || answers.length !== questions.length) {
      setAnswers(Array(questions.length).fill(null));
    }

    // Calculate final scores
    let correctCount = 0;
    let incorrectCount = 0;
    let unansweredCount = 0;

    questions.forEach((question, idx) => {
      const userAnswer = answers[idx];
      if (userAnswer === null || userAnswer === undefined || userAnswer === '') {
        unansweredCount++;
      } else if (userAnswer === question.correct) {
        correctCount++;
      } else {
        incorrectCount++;
      }
    });

    // Update state with calculated scores
    setScore(correctCount);
    setMistakes(incorrectCount);

    setShowSummary(true);
    setReviewMode(false);
    setTimerPaused(true); // Permanently pause timer when exam is finished
    if (timerRef.current) clearInterval(timerRef.current);

    await saveToHistory();
  };

  const handleCloseSummary = () => {
    setShowSummary(false);
    setReviewMode(false);
    setCurrent(0);
    setSelected(null);
    setShowResult(false);
    setShowExplanation(false);
    setScore(0);
    setMistakes(0);
    setTime(0);
    setAnswers([]);
  };

  const handleAddRanking = () => {
    // Send all relevant exam result data to backend
    const categoryId = q.categoryId;
    const seriesId = q.seriesId;
    fetch('http://localhost:4000/api/rankings', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json', Authorization: `Bearer ${localStorage.getItem('token')}` },
      body: JSON.stringify({ score, time, categoryId, seriesId, correct: score, mistakes }),
    })
      .then(async res => {
        let data;
        try {
          data = await res.json();
        } catch (e) {
          data = {};
        }
        if (!res.ok) {
          console.error('Ranking save error:', data);
          let errorMessage = 'Failed to save ranking: ' + (data?.message || res.statusText);

          // Check for authentication related errors
          if (data?.message === 'Authorization header missing' || res.status === 401) {
            errorMessage = 'Giriş yapmak zorunludur\n(Bu exam kısmında bitire bastıktan sonra çıkan yerde gözükecek)';
          }

          alert(errorMessage);
          return;
        }
        setShowSummary(false);
        navigate('/rankings');
      })
      .catch(err => {
        console.error('Ranking save error:', err);
        alert('Failed to save ranking. Network error.');
      });
  };

  const prevQuestion = () => {
    if (current > 0) {
      setCurrent(c => c - 1);
      setSelected(answers[current - 1] || null);
      setShowResult(!!answers[current - 1]);
      setShowExplanation(false);
      // If the previous question was already answered, keep timer paused
      setTimerPaused(!!answers[current - 1]);
    }
  };

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: '#dbeafe', display: 'flex', alignItems: 'center', justifyContent: 'center', position: 'relative' }}>
      {/* Timer at top right with pause indicator */}
      <Box
        sx={{
          position: 'fixed',
          top: 90,
          right: 32,
          zIndex: 1000,
          bgcolor: timerPaused ? '#ffeb3b' : '#b6d4fa',
          color: '#19376D',
          px: 2,
          py: 0.5,
          borderRadius: 2,
          fontWeight: 700,
          fontSize: 18,
          boxShadow: 2,
          display: 'flex',
          alignItems: 'center',
          gap: 1,
          transition: 'background-color 0.3s ease',
        }}
      >
        {timerPaused && <span>⏸️</span>}
        {Math.floor(time / 60)}:{(time % 60).toString().padStart(2, '0')}
      </Box>
      {/* Navigation buttons outside the card */}
      <IconButton
        sx={{
          position: 'absolute',
          left: { xs: 8, md: 32 },
          top: '50%',
          transform: 'translateY(-50%)',
          bgcolor: current > 0 ? '#e3eafc' : '#f7fafd',
          color: '#19376D',
          boxShadow: 2,
          borderRadius: 2,
          p: 2,
          cursor: current > 0 ? 'pointer' : 'default',
          opacity: current > 0 ? 1 : 0.5,
          transition: 'all 0.2s',
          '&:hover': {
            bgcolor: current > 0 ? '#b6d4fa' : '#e3eafc',
          },
        }}
        onClick={prevQuestion}
        disabled={current === 0}
      >
        <ArrowBackIcon fontSize="large" />
      </IconButton>
      <IconButton
        sx={{
          position: 'absolute',
          right: { xs: 8, md: 32 },
          top: '50%',
          transform: 'translateY(-50%)',
          bgcolor: current < questions.length - 1 ? '#e3eafc' : '#f7fafd',
          color: '#19376D',
          boxShadow: 2,
          borderRadius: 2,
          p: 2,
          cursor: current < questions.length - 1 ? 'pointer' : 'default',
          opacity: current < questions.length - 1 ? 1 : 0.5,
          transition: 'all 0.2s',
          '&:hover': {
            bgcolor: current < questions.length - 1 ? '#b6d4fa' : '#e3eafc',
          },
        }}
        onClick={nextQuestion}
        disabled={current === questions.length - 1}
      >
        <ArrowForwardIcon fontSize="large" />
      </IconButton>
      {!reviewMode && (
        <Paper elevation={6} sx={{ p: 5, borderRadius: 4, minWidth: 340, maxWidth: 600, width: '100%', boxShadow: 6 }}>
          <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', mb: 2 }}>
            <Typography variant="h5" fontWeight={700} align="center">Soru {current + 1} / {questions.length}</Typography>
          </Box>
          <Typography variant="h6" mb={2}>{q.text}</Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mb: 3 }}>
            {options.length > 0 ? (
              options.map((opt, idx) => {
                const isSelected = selected === opt;
                const isCorrect = opt === q.correct;
                const showColor = !!selected;
                return (
                  <Button
                    key={idx}
                    variant={isSelected ? 'contained' : 'outlined'}
                    color={showColor ? (isCorrect ? 'primary' : isSelected ? 'error' : 'primary') : 'primary'}
                    sx={{
                      textAlign: 'left',
                      fontWeight: 600,
                      fontSize: 18,
                      borderRadius: 4,
                      py: 2,
                      px: 2,
                      boxShadow: isSelected ? '0 2px 12px rgba(67,234,124,0.10)' : undefined,
                      background: showColor && isSelected
                        ? (isCorrect
                            ? 'linear-gradient(90deg,#eafaf3,#43ea7c)'
                            : 'linear-gradient(90deg,#fff0f0,#e74c3c)')
                        : undefined,
                      color: showColor && isSelected ? '#19376D' : undefined,
                      border: showColor && isSelected
                        ? (isCorrect ? '2px solid #43ea7c' : '2px solid #e74c3c')
                        : undefined,
                      position: 'relative',
                      transition: 'none',
                      '&:hover': {
                        background: !showColor ? '#b6d4fa !important' : undefined,
                        color: !showColor ? '#19376D !important' : undefined,
                        boxShadow: '0 2px 12px rgba(25,55,109,0.10)',
                        transition: 'none',
                      },
                    }}
                    onClick={() => !showResult && handleSelect(opt)}
                    disabled={!!selected}
                  >
                    <span style={{ position: 'relative', zIndex: 2 }}>{String.fromCharCode(65 + idx)}) {opt}</span>
                    {showColor && isSelected && (
                      <span style={{
                        position: 'absolute',
                        right: 16,
                        top: '50%',
                        transform: 'translateY(-50%)',
                        fontWeight: 700,
                        fontSize: 18,
                        color: isCorrect ? '#43ea7c' : '#e74c3c',
                        background: isCorrect ? '#eafaf3' : '#fff0f0',
                        borderRadius: 8,
                        padding: '2px 12px',
                        boxShadow: '0 2px 8px rgba(67,234,124,0.08)',
                        zIndex: 3,
                      }}>
                        {isCorrect ? 'Doğru' : 'Yanlış'}
                      </span>
                    )}
                  </Button>
                );
              })
            ) : (
              <Typography color="error" fontWeight={600} mb={2}>Bu sorunun seçenekleri eksik veya hatalı.</Typography>
            )}
          </Box>
          {showResult && (
            <Box sx={{ mb: 2 }}>
              <Typography color={selected === q.correct ? 'success.main' : 'error.main'} fontWeight={700} mb={1}>
                {selected === q.correct ? 'Doğru!' : `Cevabınız Yanlış. Doğru Yanıt: ${getCorrectAnswerText()}`}
              </Typography>
              <Button variant="text" onClick={() => setShowExplanation(e => !e)}>
                {showExplanation ? 'Çözümü Gizle' : 'Çözümü Göster'}
              </Button>
              {showExplanation && (
                <Typography mt={2} color="text.secondary">{q.explanation}</Typography>
              )}
            </Box>
          )}
          {/* Bitir button, only show if not finished */}
          {!showSummary && (
            <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
              <Button
                variant="contained"
                color="success"
                sx={{ fontWeight: 700, fontSize: 18, borderRadius: 3, px: 4, py: 1.5, boxShadow: 2, textTransform: 'none' }}
                onClick={handleFinish}
              >
                Bitir
              </Button>
            </Box>
          )}
        </Paper>
      )}
      {/* Result popup bubble with question review */}
      {showSummary && (
        <Box sx={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', bgcolor: 'rgba(10,29,86,0.7)', zIndex: 9999, display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <Box sx={{ bgcolor: '#fff', borderRadius: 6, boxShadow: 8, p: 4, minWidth: 320, maxWidth: '90vw', textAlign: 'center', position: 'relative' }}>
            <Typography variant="h5" fontWeight={700} color="#19376D" mb={2}>Sınav Sonucu</Typography>
            <Typography fontSize={20} fontWeight={600} color="#43ea7c" mb={1}>Doğru: {score}</Typography>
            <Typography fontSize={20} fontWeight={600} color="#e74c3c" mb={1}>Yanlış: {mistakes}</Typography>
            <Typography fontSize={20} fontWeight={600} color="#f39c12" mb={1}>Boş: {answers.filter(answer => answer === null || answer === undefined || answer === '').length}</Typography>
            <Typography fontSize={18} fontWeight={500} color="#19376D" mb={2}>Süre: {Math.floor(time / 60)}:{(time % 60).toString().padStart(2, '0')}</Typography>
            {/* Question review list */}
            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, justifyContent: 'center', mb: 3 }}>
              {questions.map((question, idx) => {
                const userAnswer = answers[idx];
                const isUnanswered = userAnswer === null || userAnswer === undefined || userAnswer === '';
                const isCorrect = !isUnanswered && userAnswer === question.correct;

                // Determine colors based on answer status
                let borderColor, textColor, backgroundColor, hoverColor;
                if (isUnanswered) {
                  // Boş bırakılan sorular - turuncu
                  borderColor = '#f39c12';
                  textColor = '#f39c12';
                  backgroundColor = '#fdf6e3';
                  hoverColor = '#f8e49a';
                } else if (isCorrect) {
                  // Doğru cevaplar - yeşil
                  borderColor = '#43ea7c';
                  textColor = '#43ea7c';
                  backgroundColor = '#eafaf3';
                  hoverColor = '#c6f7e2';
                } else {
                  // Yanlış cevaplar - kırmızı
                  borderColor = '#e74c3c';
                  textColor = '#e74c3c';
                  backgroundColor = '#fff0f0';
                  hoverColor = '#ffe3e3';
                }

                return (
                  <Button
                    key={idx}
                    variant="outlined"
                    sx={{
                      minWidth: 48,
                      height: 48,
                      fontWeight: 700,
                      fontSize: 18,
                      borderRadius: 2,
                      borderColor: borderColor,
                      color: textColor,
                      background: backgroundColor,
                      boxShadow: `0 2px 8px ${borderColor}20`,
                      mx: 0.5,
                      my: 0.5,
                      transition: 'all 0.2s',
                      '&:hover': {
                        background: hoverColor,
                      },
                    }}
                    onClick={() => {
                      setReviewMode(true);
                      setShowSummary(false);
                      setCurrent(idx);
                      setSelected(answers[idx] || null);
                      setShowResult(!!answers[idx]);
                      setShowExplanation(false);
                    }}
                  >
                    {idx + 1}
                  </Button>
                );
              })}
            </Box>
            <Typography fontSize={15} color="#19376D" mb={2}>
              Soruları incelemek için tıklayın. Yeşil: Doğru, Kırmızı: Yanlış, Turuncu: Boş
            </Typography>
            {/* Action buttons */}
            <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 2, flexWrap: 'wrap' }}>
              <Button
                variant="contained"
                color="success"
                sx={{ fontWeight: 700, fontSize: 16, borderRadius: 3, px: 4, py: 1.2, boxShadow: 2, textTransform: 'none' }}
                onClick={handleAddRanking}
              >
                Sıralamaya Ekle
              </Button>
              <Button
                variant="outlined"
                color="primary"
                sx={{ fontWeight: 700, fontSize: 16, borderRadius: 3, px: 4, py: 1.2, boxShadow: 2, textTransform: 'none' }}
                onClick={() => {
                  navigate('/categories');
                }}
              >
                Kapat
              </Button>
              <Button
                variant="contained"
                color="primary"
                sx={{ fontWeight: 700, fontSize: 16, borderRadius: 3, px: 4, py: 1.2, boxShadow: 2, textTransform: 'none' }}
                onClick={() => {
                  handleCloseSummary();
                }}
              >
                Tekrar Çöz
              </Button>
            </Box>
          </Box>
        </Box>
      )}
      {reviewMode && (
        <Paper elevation={6} sx={{ p: 5, borderRadius: 4, minWidth: 340, maxWidth: 600, width: '100%', boxShadow: 6 }}>
          <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', mb: 2 }}>
            <Typography variant="h5" fontWeight={700} align="center">Soru {current + 1} / {questions.length}</Typography>
          </Box>
          <Typography variant="h6" mb={2}>{q.text}</Typography>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mb: 3 }}>
            {options.length > 0 ? (
              options.map((opt, idx) => {
                const isSelected = answers[current] === opt;
                const isCorrect = opt === q.correct;
                const showColor = !!answers[current];
                return (
                  <Button
                    key={idx}
                    variant={isSelected ? 'contained' : 'outlined'}
                    color={showColor ? (isCorrect ? 'primary' : isSelected ? 'error' : 'primary') : 'primary'}
                    sx={{
                      textAlign: 'left',
                      fontWeight: 600,
                      fontSize: 18,
                      borderRadius: 4,
                      py: 2,
                      px: 2,
                      boxShadow: isSelected ? '0 2px 12px rgba(67,234,124,0.10)' : undefined,
                      background: showColor && isSelected
                        ? (isCorrect
                            ? 'linear-gradient(90deg,#eafaf3,#43ea7c)'
                            : 'linear-gradient(90deg,#fff0f0,#e74c3c)')
                        : undefined,
                      color: showColor && isSelected ? '#19376D' : undefined,
                      border: showColor && isSelected
                        ? (isCorrect ? '2px solid #43ea7c' : '2px solid #e74c3c')
                        : undefined,
                      position: 'relative',
                      transition: 'none',
                      '&:hover': {
                        background: !showColor ? '#b6d4fa !important' : undefined,
                        color: !showColor ? '#19376D !important' : undefined,
                        boxShadow: '0 2px 12px rgba(25,55,109,0.10)',
                        transition: 'none',
                      },
                    }}
                    disabled
                  >
                    <span style={{ position: 'relative', zIndex: 2 }}>{String.fromCharCode(65 + idx)}) {opt}</span>
                    {showColor && isSelected && (
                      <span style={{
                        position: 'absolute',
                        right: 16,
                        top: '50%',
                        transform: 'translateY(-50%)',
                        fontWeight: 700,
                        fontSize: 18,
                        color: isCorrect ? '#43ea7c' : '#e74c3c',
                        background: isCorrect ? '#eafaf3' : '#fff0f0',
                        borderRadius: 8,
                        padding: '2px 12px',
                        boxShadow: '0 2px 8px rgba(67,234,124,0.08)',
                        zIndex: 3,
                      }}>
                        {isCorrect ? 'Doğru' : 'Yanlış'}
                      </span>
                    )}
                  </Button>
                );
              })
            ) : (
              <Typography color="error" fontWeight={600} mb={2}>Bu sorunun seçenekleri eksik veya hatalı.</Typography>
            )}
          </Box>
          {/* Show explanation toggle and content in review mode */}
          {!!q.explanation && (
            <Box sx={{ mb: 2 }}>
              <Button variant="text" onClick={() => setShowExplanation(e => !e)}>
                {showExplanation ? 'Çözümü Gizle' : 'Çözümü Göster'}
              </Button>
              {showExplanation && (
                <Typography mt={2} color="text.secondary">{q.explanation}</Typography>
              )}
            </Box>
          )}
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <Button
              variant="outlined"
              color="primary"
              sx={{ fontWeight: 700, fontSize: 16, borderRadius: 3, px: 4, py: 1.2, boxShadow: 2, textTransform: 'none' }}
              onClick={() => {
                setReviewMode(false);
                setShowSummary(true);
                setShowExplanation(false);
              }}
            >
              Sonuçlara Dön
            </Button>
          </Box>
        </Paper>
      )}
    </Box>
  );
};

export default Exam;
