import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Button,
  LinearProgress,
  Chip,
  Alert,
  Fade,
  Card,
  CardContent,
} from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import RefreshIcon from '@mui/icons-material/Refresh';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import { useNavigate, useSearchParams } from 'react-router-dom';

interface WordPair {
  id: number;
  turkish: string;
  english: string;
  isMatched: boolean;
}

interface GameCard {
  id: number;
  word: string;
  type: 'turkish' | 'english';
  pairId: number;
  isSelected: boolean;
  isMatched: boolean;
}

const A1_WORDS: WordPair[] = [
  { id: 1, turkish: 'merhaba', english: 'hello', isMatched: false },
  { id: 2, turkish: 'dünya', english: 'world', isMatched: false },
  { id: 3, turkish: 'aile', english: 'family', isMatched: false },
  { id: 4, turkish: 'okul', english: 'school', isMatched: false },
  { id: 5, turkish: 'kitap', english: 'book', isMatched: false },
  { id: 6, turkish: 'su', english: 'water', isMatched: false },
  { id: 7, turkish: 'ev', english: 'house', isMatched: false },
  { id: 8, turkish: 'araba', english: 'car', isMatched: false },
  { id: 9, turkish: 'yemek', english: 'food', isMatched: false },
  { id: 10, turkish: 'zaman', english: 'time', isMatched: false },
  { id: 11, turkish: 'para', english: 'money', isMatched: false },
  { id: 12, turkish: 'çalışma', english: 'work', isMatched: false },
  { id: 13, turkish: 'oyun', english: 'game', isMatched: false },
  { id: 14, turkish: 'müzik', english: 'music', isMatched: false },
  { id: 15, turkish: 'hayat', english: 'life', isMatched: false },
];

const LEVELS = [
  { value: 'A1', label: 'A1 - Başlangıç' },
  { value: 'A2', label: 'A2 - Temel' },
  { value: 'B1', label: 'B1 - Orta Alt' },
  { value: 'B2', label: 'B2 - Orta Üst' },
];

export default function WordMatchingGame() {
  const navigate = useNavigate();
  const [searchParams] = useSearchParams();
  const level = searchParams.get('level') || 'a1';
  const selectedLevel = level.toUpperCase();

  const [gameCards, setGameCards] = useState<GameCard[]>([]);
  const [selectedCards, setSelectedCards] = useState<GameCard[]>([]);
  const [matchedPairs, setMatchedPairs] = useState<number>(0);
  const [gameCompleted, setGameCompleted] = useState<boolean>(false);
  const [timer, setTimer] = useState<number>(0);
  const [isPlaying, setIsPlaying] = useState<boolean>(false);
  const [wrongMatch, setWrongMatch] = useState<boolean>(false);
  const [correctMatch, setCorrectMatch] = useState<boolean>(false);

  // Start game on component mount
  useEffect(() => {
    startGame();
  }, []);

  // Timer effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isPlaying && !gameCompleted) {
      interval = setInterval(() => {
        setTimer(prev => prev + 1);
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isPlaying, gameCompleted]);

  // Format timer
  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  // Shuffle array
  const shuffleArray = (array: any[]) => {
    const newArray = [...array];
    for (let i = newArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [newArray[i], newArray[j]] = [newArray[j], newArray[i]];
    }
    return newArray;
  };

  const createGameCards = () => {
    const cards: GameCard[] = [];
    A1_WORDS.forEach((word, index) => {
      cards.push({
        id: index * 2,
        word: word.turkish,
        type: 'turkish',
        pairId: word.id,
        isSelected: false,
        isMatched: false,
      });
      cards.push({
        id: index * 2 + 1,
        word: word.english,
        type: 'english',
        pairId: word.id,
        isSelected: false,
        isMatched: false,
      });
    });
    return shuffleArray(cards);
  };

  const startGame = () => {
    const cards = createGameCards();
    setGameCards(cards);
    setMatchedPairs(0);
    setGameCompleted(false);
    setSelectedCards([]);
    setTimer(0);
    setIsPlaying(true);
  };

  const handleCardClick = (clickedCard: GameCard) => {
    if (clickedCard.isMatched || clickedCard.isSelected || selectedCards.length === 2) {
      return;
    }

    const newSelectedCards = [...selectedCards, clickedCard];
    setSelectedCards(newSelectedCards);

    // Update card selection state
    setGameCards(prev => prev.map(card => 
      card.id === clickedCard.id 
        ? { ...card, isSelected: true }
        : card
    ));

    if (newSelectedCards.length === 2) {
      setTimeout(() => checkMatch(newSelectedCards), 500);
    }
  };

  const checkMatch = (cards: GameCard[]) => {
    const [card1, card2] = cards;

    if (card1.pairId === card2.pairId && card1.type !== card2.type) {
      // Correct match
      setGameCards(prev => prev.map(card => 
        card.pairId === card1.pairId 
          ? { ...card, isMatched: true, isSelected: false }
          : { ...card, isSelected: false }
      ));
      setMatchedPairs(prev => prev + 1);
      setCorrectMatch(true);

      setTimeout(() => {
        setCorrectMatch(false);
        if (matchedPairs + 1 === 15) {
          setGameCompleted(true);
          setIsPlaying(false);
        }
      }, 1000);
    } else {
      // Wrong match
      setWrongMatch(true);
      setGameCards(prev => prev.map(card => ({ ...card, isSelected: false })));
      setTimeout(() => {
        setWrongMatch(false);
      }, 1000);
    }

    setSelectedCards([]);
  };

  const resetGame = () => {
    startGame();
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: '#b2dfdb',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        pb: { xs: 12, md: 16 },
        pt: 0,
        px: 2,
      }}
    >
      <Paper
        elevation={6}
        sx={{
          p: 0,
          borderRadius: 4,
          minWidth: 340,
          width: '100%',
          maxWidth: 900,
          mt: { xs: 1, md: '15px' },
          background: 'linear-gradient(135deg, rgba(255,255,255,0.95) 0%, rgba(255,255,255,0.9) 100%)',
          backdropFilter: 'blur(10px)',
          border: '1px solid rgba(255, 255, 255, 0.2)',
          boxShadow: '0 20px 40px rgba(0, 0, 0, 0.1)',
        }}
      >
        {/* Header */}
        <Box
          sx={{
            background: 'linear-gradient(135deg, #00b894 0%, #00cec9 100%)',
            color: '#fff',
            p: { xs: 3, md: 4 },
            borderTopLeftRadius: 'inherit',
            borderTopRightRadius: 'inherit',
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
          }}
        >
          <Button
            startIcon={<ArrowBackIcon />}
            onClick={() => navigate('/kelime-eslestirme')}
            sx={{ color: '#fff', fontWeight: 600 }}
          >
            Geri
          </Button>

          <Box sx={{ textAlign: 'center', flex: 1 }}>
            <Typography variant="h5" fontWeight={700} mb={1}>
              Kelime Eşleştirme
            </Typography>
            <Typography variant="body2" sx={{ opacity: 0.9 }}>
              Seviye: {selectedLevel} • Süre: {formatTime(timer)}
            </Typography>
          </Box>

          <Button
            startIcon={<RefreshIcon />}
            onClick={resetGame}
            sx={{ color: '#fff', fontWeight: 600 }}
            disabled={!isPlaying && !gameCompleted}
          >
            Yenile
          </Button>
        </Box>

        {/* Progress */}
        <Box sx={{ p: 3 }}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6" color="#00695c" fontWeight={600}>
              İlerleme: {matchedPairs}/15
            </Typography>
            <Chip 
              label={`${Math.round((matchedPairs / 15) * 100)}%`}
              color="primary"
              sx={{ 
                fontWeight: 600,
                background: 'linear-gradient(90deg, #00b894 0%, #00cec9 100%)',
                color: '#fff',
              }}
            />
          </Box>
          <LinearProgress 
            variant="determinate" 
            value={(matchedPairs / 15) * 100}
            sx={{ 
              height: 8, 
              borderRadius: 4,
              backgroundColor: '#e0f2f1',
              '& .MuiLinearProgress-bar': {
                background: 'linear-gradient(90deg, #00b894 0%, #00cec9 100%)',
                borderRadius: 4,
              }
            }}
          />
        </Box>

        {/* Status Messages */}
        <Box sx={{ px: 3 }}>
          {wrongMatch && (
            <Fade in={wrongMatch}>
              <Alert 
                severity="error" 
                icon={<ErrorIcon />}
                sx={{ mb: 2, borderRadius: 2 }}
              >
                Yanlış eşleşme! Tekrar deneyin.
              </Alert>
            </Fade>
          )}

          {correctMatch && (
            <Fade in={correctMatch}>
              <Alert 
                severity="success" 
                icon={<CheckCircleIcon />}
                sx={{ mb: 2, borderRadius: 2 }}
              >
                Harika! Doğru eşleşme!
              </Alert>
            </Fade>
          )}

          {gameCompleted && (
            <Alert 
              severity="success" 
              icon={<CheckCircleIcon />}
              sx={{ mb: 3, borderRadius: 2, fontSize: 16 }}
            >
              <strong>Tebrikler!</strong> Oyunu {formatTime(timer)} sürede tamamladınız!
            </Alert>
          )}
        </Box>

        {/* Game Cards */}
        <Box sx={{ p: 3, pt: 0 }}>
          <Box sx={{ 
            display: 'grid', 
            gridTemplateColumns: { xs: 'repeat(3, 1fr)', sm: 'repeat(4, 1fr)', md: 'repeat(6, 1fr)' },
            gap: 1.5,
            mb: 3
          }}>
            {gameCards.map((card) => (
              <Card
                key={card.id}
                onClick={() => handleCardClick(card)}
                sx={{
                  minHeight: 70,
                  cursor: card.isMatched ? 'default' : 'pointer',
                  borderRadius: 2,
                  background: card.isMatched 
                    ? 'linear-gradient(135deg, #4CAF50 0%, #66BB6A 100%)'
                    : card.isSelected
                    ? 'linear-gradient(135deg, #00b894 0%, #00cec9 100%)'
                    : card.type === 'turkish'
                    ? 'linear-gradient(135deg, #2196F3 0%, #42A5F5 100%)'
                    : 'linear-gradient(135deg, #9C27B0 0%, #BA68C8 100%)',
                  color: '#fff',
                  border: card.isSelected ? '2px solid #00695c' : 'none',
                  boxShadow: card.isMatched ? '0 2px 8px rgba(76, 175, 80, 0.3)' : '0 1px 4px rgba(0,0,0,0.1)',
                  transition: 'all 0.2s ease',
                  '&:hover': {
                    transform: card.isMatched ? 'none' : 'scale(1.03)',
                    boxShadow: card.isMatched ? '0 2px 8px rgba(76, 175, 80, 0.3)' : '0 2px 8px rgba(0,0,0,0.15)',
                  },
                  '&:active': {
                    transform: card.isMatched ? 'none' : 'scale(0.98)',
                  },
                }}
              >
                <CardContent sx={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  justifyContent: 'center',
                  minHeight: 70,
                  p: 1,
                  '&:last-child': { pb: 1 }
                }}>
                  <Typography 
                    variant="body2" 
                    fontWeight={600}
                    textAlign="center"
                    sx={{ fontSize: { xs: '12px', sm: '14px' } }}
                  >
                    {card.word}
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Box>

          {/* Game Actions */}
          <Box sx={{ display: 'flex', justifyContent: 'center', gap: 2, mt: 3 }}>
            <Button
              variant="outlined"
              onClick={() => navigate('/kelime-eslestirme')}
              sx={{
                borderColor: '#00b894',
                color: '#00695c',
                fontWeight: 600,
                borderRadius: 2,
                px: 3,
                '&:hover': {
                  borderColor: '#00cec9',
                  backgroundColor: 'rgba(0, 184, 148, 0.04)',
                },
              }}
            >
              Seviye Değiştir
            </Button>

            {gameCompleted && (
              <Button
                variant="contained"
                onClick={startGame}
                sx={{
                  background: 'linear-gradient(90deg, #00b894 0%, #00cec9 100%)',
                  color: '#fff',
                  fontWeight: 600,
                  borderRadius: 2,
                  px: 3,
                  '&:hover': {
                    background: 'linear-gradient(90deg, #00cec9 0%, #00b894 100%)',
                  },
                }}
              >
                Tekrar Oyna
              </Button>
            )}
          </Box>
        </Box>
      </Paper>
    </Box>
  );
}
