import React, { useState, useRef, useEffect } from "react";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Button from "@mui/material/Button";
import IconButton from "@mui/material/IconButton";
import ArrowBackIcon from "@mui/icons-material/ArrowBack";
import Chip from "@mui/material/Chip";
import Paper from "@mui/material/Paper";
import Alert from "@mui/material/Alert";

// Paragraf soruları - her soru 4-5 cümle, toplam 5-6 boşluk
const paragraphQuestions = [
  {
    id: 1,
    title: "Technology and Communication",
    text: "Technology has dramatically changed the way we communicate with each other. Social media platforms have made it possible to connect with people from all around the world ______. However, some experts argue that digital communication lacks the ______ connection that face-to-face interactions provide. Despite this concern, many businesses have successfully adapted to remote work environments. The ability to collaborate ______ has become essential in today's global economy. As we move forward, finding the right balance between digital and ______ communication will be crucial for maintaining healthy relationships.",
    blanks: [
      { index: 0, options: ["instantly", "slowly", "rarely", "never"], correct: "instantly" },
      { index: 1, options: ["emotional", "physical", "digital", "virtual"], correct: "emotional" },
      { index: 2, options: ["separately", "offline", "remotely", "locally"], correct: "remotely" },
      { index: 3, options: ["personal", "digital", "written", "electronic"], correct: "personal" }
    ]
  },
  {
    id: 2,
    title: "Environmental Protection",
    text: "Climate change has become one of the most ______ challenges facing humanity today. Scientists around the world are working tirelessly to develop ______ energy solutions. Renewable sources like solar and wind power are becoming increasingly ______ and efficient. Many countries have committed to reducing their carbon emissions significantly over the next decade. Individual actions, such as recycling and using public transportation, also play a ______ role in protecting our environment for future generations.",
    blanks: [
      { index: 0, options: ["urgent", "easy", "simple", "minor"], correct: "urgent" },
      { index: 1, options: ["traditional", "sustainable", "expensive", "harmful"], correct: "sustainable" },
      { index: 2, options: ["expensive", "unreliable", "affordable", "dangerous"], correct: "affordable" },
      { index: 3, options: ["minor", "crucial", "negative", "harmful"], correct: "crucial" }
    ]
  },
  {
    id: 3,
    title: "Education in the Digital Age",
    text: "Online learning has ______ transformed the educational landscape in recent years. Students can now access high-quality courses from universities around the world without leaving their homes. This flexibility has made education more ______ to people from diverse backgrounds and circumstances. However, traditional classroom environments still offer unique benefits such as direct ______ with teachers and peers. The future of education will likely combine the best aspects of both online and ______ learning methods to create comprehensive educational experiences.",
    blanks: [
      { index: 0, options: ["slightly", "rapidly", "slowly", "barely"], correct: "rapidly" },
      { index: 1, options: ["expensive", "difficult", "accessible", "limited"], correct: "accessible" },
      { index: 2, options: ["conflict", "interaction", "competition", "isolation"], correct: "interaction" },
      { index: 3, options: ["online", "digital", "virtual", "traditional"], correct: "traditional" }
    ]
  }
];

function EssayGame() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userAnswers, setUserAnswers] = useState<string[]>([]);
  const [selectedAnswers, setSelectedAnswers] = useState<{ [key: number]: string }>({});
  const [showResults, setShowResults] = useState(false);
  const [timer, setTimer] = useState(0);
  const [timerActive, setTimerActive] = useState(false);
  const [gameCompleted, setGameCompleted] = useState(false);
  const timerRef = useRef<NodeJS.Timeout | null>(null);

  const question = paragraphQuestions[currentQuestion];

  useEffect(() => {
    if (timerActive && !gameCompleted) {
      timerRef.current = setInterval(() => {
        setTimer((t) => t + 1);
      }, 1000);
    }
    return () => {
      if (timerRef.current) clearInterval(timerRef.current);
    };
  }, [timerActive, gameCompleted]);

  useEffect(() => {
    if (!timerActive) setTimerActive(true);
  }, []);

  const handleAnswerSelect = (blankIndex: number, answer: string) => {
    setSelectedAnswers(prev => ({
      ...prev,
      [blankIndex]: answer
    }));
  };

  const renderTextWithBlanks = () => {
    const parts = question.text.split('______');
    const result = [];

    for (let i = 0; i < parts.length; i++) {
      result.push(
        <span key={`text-${i}`}>{parts[i]}</span>
      );

      if (i < parts.length - 1) {
        const blankIndex = i;
        const selectedAnswer = selectedAnswers[blankIndex];

        result.push(
          <Box
            key={`blank-${i}`}
            component="span"
            sx={{
              display: "inline-block",
              minWidth: 100,
              height: 32,
              mx: 1,
              px: 2,
              py: 0.5,
              bgcolor: selectedAnswer ? "#e3f2fd" : "#f5f5f5",
              border: selectedAnswer ? "2px solid #1976d2" : "2px dashed #ccc",
              borderRadius: 2,
              textAlign: "center",
              fontSize: 16,
              fontWeight: selectedAnswer ? 600 : 400,
              color: selectedAnswer ? "#1976d2" : "#999",
              cursor: "pointer",
              transition: "all 0.2s"
            }}
          >
            {selectedAnswer || `${blankIndex + 1}`}
          </Box>
        );
      }
    }

    return result;
  };

  const checkAnswers = () => {
    const results = question.blanks.map(blank => 
      selectedAnswers[blank.index] === blank.correct
    );
    setUserAnswers(results.map(r => r ? "correct" : "incorrect"));
    setShowResults(true);
  };

  const nextQuestion = () => {
    if (currentQuestion < paragraphQuestions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
      setSelectedAnswers({});
      setShowResults(false);
      setUserAnswers([]);
    } else {
      setGameCompleted(true);
      if (timerRef.current) clearInterval(timerRef.current);
    }
  };

  const restartGame = () => {
    setCurrentQuestion(0);
    setSelectedAnswers({});
    setShowResults(false);
    setUserAnswers([]);
    setGameCompleted(false);
    setTimer(0);
    setTimerActive(true);
  };

  const getScore = () => {
    return userAnswers.filter(answer => answer === "correct").length;
  };

  const isAllAnswered = () => {
    return question.blanks.every(blank => selectedAnswers[blank.index]);
  };

  if (gameCompleted) {
    return (
      <Box sx={{ minHeight: "100vh", bgcolor: "#b2dfdb", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", px: 2 }}>
        <Paper sx={{ p: 4, maxWidth: 600, width: "100%", textAlign: "center", borderRadius: 4 }}>
          <Typography variant="h4" fontWeight={700} mb={2} color="#19376D">
            🎉 Tebrikler!
          </Typography>
          <Typography variant="h6" mb={2}>
            Tüm paragrafları tamamladınız!
          </Typography>
          <Typography variant="h5" fontWeight={600} mb={3} color="#1976d2">
            Süre: {`${String(Math.floor(timer / 60)).padStart(2, '0')}:${String(timer % 60).padStart(2, '0')}`}
          </Typography>
          <Box sx={{ display: "flex", gap: 2, justifyContent: "center" }}>
            <Button
              variant="contained"
              onClick={restartGame}
              sx={{ bgcolor: "#19376D", "&:hover": { bgcolor: "#0f2149" } }}
            >
              Tekrar Oyna
            </Button>
            <Button
              variant="outlined"
              onClick={() => window.location.href = '/questions'}
              sx={{ color: "#19376D", borderColor: "#19376D" }}
            >
              Ana Menü
            </Button>
          </Box>
        </Paper>
      </Box>
    );
  }

  return (
    <Box sx={{ minHeight: "100vh", bgcolor: "#b2dfdb", display: "flex", flexDirection: "column", alignItems: "center", px: 2, pt: { xs: 3, sm: 5 } }}>
      <Box sx={{ width: '100%', maxWidth: { xs: 700, md: 900 }, bgcolor: "#fff", borderRadius: 4, boxShadow: 6, p: { xs: 3, md: 4 }, mb: 2, position: "relative" }}>

        {/* Header */}
        <Box sx={{ display: "flex", alignItems: "center", justifyContent: "space-between", mb: 3 }}>
          <IconButton 
            sx={{ color: "#19376D" }} 
            onClick={() => window.location.href = '/questions'}
          >
            <ArrowBackIcon />
          </IconButton>
          <Typography variant="h6" fontWeight={700} color="#19376D">
            {question.title} ({currentQuestion + 1}/{paragraphQuestions.length})
          </Typography>
          <Typography variant="h6" fontWeight={700} color="#19376D">
            {`${String(Math.floor(timer / 60)).padStart(2, '0')}:${String(timer % 60).padStart(2, '0')}`}
          </Typography>
        </Box>

        {/* Progress Bar */}
        <Box sx={{ width: "100%", height: 6, bgcolor: "#e0e0e0", borderRadius: 3, mb: 4 }}>
          <Box
            sx={{
              width: `${((currentQuestion + 1) / paragraphQuestions.length) * 100}%`,
              height: "100%",
              bgcolor: "#19376D",
              borderRadius: 3,
              transition: "width 0.3s"
            }}
          />
        </Box>

        {/* Paragraph with blanks */}
        <Paper sx={{ p: 3, mb: 4, bgcolor: "#f9f9f9", borderRadius: 3 }}>
          <Typography 
            variant="h6" 
            sx={{ 
              lineHeight: 2, 
              fontSize: { xs: 16, md: 18 },
              color: "#333"
            }}
          >
            {renderTextWithBlanks()}
          </Typography>
        </Paper>

        {/* Answer Options */}
        <Box sx={{ mb: 4 }}>
          <Typography variant="h6" fontWeight={600} mb={2} color="#19376D">
            Kelime Seçenekleri:
          </Typography>
          {question.blanks.map((blank, index) => (
            <Box key={index} sx={{ mb: 3 }}>
              <Typography variant="subtitle1" fontWeight={500} mb={1} color="#555">
                Boşluk {blank.index + 1}:
              </Typography>
              <Box sx={{ display: "flex", flexWrap: "wrap", gap: 1 }}>
                {blank.options.map((option) => (
                  <Chip
                    key={option}
                    label={option}
                    onClick={() => handleAnswerSelect(blank.index, option)}
                    sx={{
                      bgcolor: selectedAnswers[blank.index] === option ? "#1976d2" : "#f5f5f5",
                      color: selectedAnswers[blank.index] === option ? "white" : "#333",
                      fontWeight: selectedAnswers[blank.index] === option ? 600 : 400,
                      cursor: "pointer",
                      "&:hover": {
                        bgcolor: selectedAnswers[blank.index] === option ? "#1565c0" : "#e0e0e0"
                      }
                    }}
                  />
                ))}
              </Box>
            </Box>
          ))}
        </Box>

        {/* Results */}
        {showResults && (
          <Alert 
            severity={getScore() === question.blanks.length ? "success" : "warning"} 
            sx={{ mb: 3 }}
          >
            <Typography fontWeight={600}>
              Sonuç: {getScore()}/{question.blanks.length} doğru cevap!
            </Typography>
            {question.blanks.map((blank, index) => (
              <Typography key={index} variant="body2" sx={{ mt: 1 }}>
                Boşluk {blank.index + 1}: <strong>{blank.correct}</strong>
                {selectedAnswers[blank.index] !== blank.correct && (
                  <span style={{ color: "#d32f2f" }}> (Seçiminiz: {selectedAnswers[blank.index]})</span>
                )}
              </Typography>
            ))}
          </Alert>
        )}

        {/* Action Buttons */}
        <Box sx={{ display: "flex", gap: 2, justifyContent: "center" }}>
          {!showResults ? (
            <Button
              variant="contained"
              onClick={checkAnswers}
              disabled={!isAllAnswered()}
              sx={{
                bgcolor: "#19376D",
                "&:hover": { bgcolor: "#0f2149" },
                px: 4,
                py: 1.5,
                fontSize: 16,
                fontWeight: 600
              }}
            >
              Cevapları Kontrol Et
            </Button>
          ) : (
            <Button
              variant="contained"
              onClick={nextQuestion}
              sx={{
                bgcolor: "#1976d2",
                "&:hover": { bgcolor: "#1565c0" },
                px: 4,
                py: 1.5,
                fontSize: 16,
                fontWeight: 600
              }}
            >
              {currentQuestion < paragraphQuestions.length - 1 ? "Sonraki Paragraf" : "Oyunu Bitir"}
            </Button>
          )}
        </Box>
      </Box>
    </Box>
  );
}

export default EssayGame;
