import React, { useEffect, useState } from 'react';
import { Box, Paper, Typography, Button, TextField } from '@mui/material';


interface Props {
  token: string;
}

const medalIcons = [
  '🥇', // 1st
  '🥈', // 2nd
  '🥉', // 3rd
];

const examOptions = ['A1', 'A2', 'B1', 'B2'];

// Demo categories for Autocomplete
const categories = [
  { title: 'Matematik', firstLetter: 'M' },
  { title: 'Fizik', firstLetter: 'F' },
  { title: 'Kimya', firstLetter: 'K' },
  { title: 'Biyoloji', firstLetter: 'B' },
  { title: 'Tarih', firstLetter: 'T' },
  { title: 'Coğrafya', firstLetter: 'C' },
  { title: 'Edebiyat', firstLetter: 'E' },
];

const Rankings: React.FC<Props> = ({ token }) => {
  const [rankings, setRankings] = useState<any[]>([]);
  const [selectedExam, setSelectedExam] = useState<string>('A1');
  const [loading, setLoading] = useState<boolean>(false);
  const [comments, setComments] = useState<any[]>([]);
  const [commentText, setCommentText] = useState('');
  const [commentLoading, setCommentLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost:4000/api/rankings", {
      headers: token ? { Authorization: `Bearer ${token}` } : {},
    })
      .then((res) => res.json())
      .then((data) => {
        setRankings(data.rankings || []);
        setLoading(false);
      })
      .catch((err) => {
        setRankings([]);
        setLoading(false);
      });
    // Fetch comments for selected exam
    fetch(`http://localhost:4000/api/comments?exam=${selectedExam}`, {
      headers: token ? { Authorization: `Bearer ${token}` } : {}
    })
      .then(res => res.json())
      .then(data => setComments(data.comments || []))
      .catch(() => setComments([]));
  }, [token, selectedExam]);

  // Only show top 20
  const topRankings = rankings.slice(0, 20);

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'flex-start', minHeight: '100vh', bgcolor: '#b2dfdb', pt: { xs: 7, md: 8 }, px: { xs: 1, md: 4 } }}>
      <Box sx={{ background: '#fff', color: '#222', p: { xs: 3, md: 4 }, borderRadius: 4, boxShadow: 4, minWidth: 340, maxWidth: 600, width: '100%', mt: { xs: -3, md: -5 } }}>
        {/* ...existing code... */}
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginBottom: 16 }}>
          <label htmlFor="exam-select" style={{ fontWeight: 600, marginRight: 8, fontSize: 18 }}>Sınav:</label>
          <select
            id="exam-select"
            value={selectedExam}
            onChange={e => setSelectedExam(e.target.value)}
            style={{ fontSize: 18, padding: '4px 12px', borderRadius: 8, border: '1px solid #bcd', background: '#f7fbff', fontWeight: 600 }}
          >
            {examOptions.map(opt => (
              <option key={opt} value={opt}>{opt}</option>
            ))}
          </select>
        </div>
        <h2 style={{ textAlign: 'center', fontWeight: 700, fontSize: 32, marginBottom: 16 }}>
          <span role="img" aria-label="trophy">🏆</span> Sıralama Tablosu
        </h2>
        {loading ? (
          <div style={{ textAlign: 'center', padding: 32, fontSize: 20 }}>Yükleniyor...</div>
        ) : rankings.length === 0 ? (
          <div style={{ textAlign: 'center', padding: 32, fontSize: 20 }}>Hiç sıralama bulunamadı.</div>
        ) : (
          <table style={{ width: '100%', borderCollapse: 'separate', borderSpacing: 0, fontSize: 18, background: 'transparent' }}>
            <thead>
              <tr style={{ background: 'linear-gradient(90deg, #e3f0ff 0%, #f7fbff 100%)' }}>
                <th style={{ padding: '10px 0', fontWeight: 600, color: '#2a4d7a' }}>Sıra</th>
                <th style={{ padding: '10px 0', fontWeight: 600, color: '#2a4d7a' }}>İsim</th>
                <th style={{ padding: '10px 0', fontWeight: 600, color: '#2a4d7a' }}>Puan</th>
                <th style={{ padding: '10px 0', fontWeight: 600, color: '#2a4d7a' }}>Doğru</th>
                <th style={{ padding: '10px 0', fontWeight: 600, color: '#2a4d7a' }}>Yanlış</th>
                <th style={{ padding: '10px 0', fontWeight: 600, color: '#2a4d7a' }}>Süre</th>
              </tr>
            </thead>
            <tbody>
              {topRankings.map((r, idx) => (
                <tr key={r.id || idx} style={{ borderBottom: '1px solid #f0f0f0', background: idx % 2 === 0 ? '#fcfcfc' : '#fff' }}>
                  <td style={{ textAlign: 'center', fontWeight: 600, fontSize: 20 }}>
                    {idx < 3 ? (
                      <span style={{ fontSize: 28 }}>{medalIcons[idx]}</span>
                    ) : (
                      <span style={{ color: '#7b8fa6', fontWeight: 700 }}>{idx + 1}</span>
                    )}
                  </td>
                  <td style={{ fontWeight: idx < 3 ? 700 : 500, color: idx < 3 ? '#222' : '#444' }}>{r.user?.name || 'No name'}</td>
                  <td style={{ fontWeight: 700, color: '#1a3e72', fontSize: 20 }}>{r.score}</td>
                  <td style={{ color: '#1a3e72', fontWeight: 500 }}>{r.correct ?? '-'}</td>
                  <td style={{ color: '#e74c3c', fontWeight: 500 }}>{r.mistakes ?? '-'}</td>
                  <td style={{ color: '#1a3e72', fontWeight: 500 }}>{r.time ?? '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
        {/* Comments Section */}
        <Box sx={{ mt: { xs: 6, md: 8 } }}>
        <Box sx={{ mt: { xs: 6, md: 8 }, bgcolor: '#f7fbff', borderRadius: 4, boxShadow: 2, p: { xs: 2, md: 3 }, border: '1px solid #e0e7ef' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6" fontWeight={700} color="#19376D" sx={{ flex: 1 }}>
              {comments.length} Yorum
            </Typography>
          </Box>
          {/* Input bar (only for logged in users) */}
          {token && (
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 3, bgcolor: '#fff', borderRadius: 3, boxShadow: 1, px: 2, py: 1 }}>
              <Box sx={{ mr: 2 }}>
                <img
                  src={"/uploads/profile-photos/" + (window.localStorage.getItem('profilePhoto') || 'logo192.png')}
                  alt="profile"
                  style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover', border: '2px solid #19376D', background: '#fff' }}
                />
              </Box>
              <TextField
                fullWidth
                variant="outlined"
                placeholder="Type your reply"
                value={commentText}
                onChange={e => setCommentText(e.target.value)}
                sx={{ bgcolor: '#f7fbff', borderRadius: 2, mr: 2 }}
                inputProps={{ style: { fontSize: 16 } }}
              />
              <Button
                variant="contained"
                sx={{ borderRadius: 2, fontWeight: 700, background: '#3b82f6', color: '#fff', px: 3, py: 1, boxShadow: 1, textTransform: 'none', fontSize: 16 }}
                disabled={commentLoading || !commentText.trim()}
                onClick={async () => {
                  setCommentLoading(true);
                  await fetch('http://localhost:4000/api/comments', {
                    method: 'POST',
                    headers: {
                      'Content-Type': 'application/json',
                      ...(token ? { Authorization: `Bearer ${token}` } : {})
                    },
                    body: JSON.stringify({ text: commentText, exam: selectedExam })
                  });
                  setCommentText('');
                  setCommentLoading(false);
                  // Optionally, refetch comments
                  fetch(`http://localhost:4000/api/comments?exam=${selectedExam}`, {
                    headers: token ? { Authorization: `Bearer ${token}` } : {}
                  })
                    .then(res => res.json())
                    .then(data => setComments(data.comments || []))
                    .catch(() => setComments([]));
                }}
              >Post</Button>
            </Box>
          )}
          {/* Comments list */}
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            {comments.length === 0 ? (
              <Typography color="#888" fontSize={15} align="center">No comments yet.</Typography>
            ) : (
              comments.map((c, i) => (
                <Box key={c.id || i} sx={{ display: 'flex', alignItems: 'flex-start', bgcolor: '#fff', borderRadius: 3, boxShadow: 1, p: 2, border: '1px solid #e0e7ef' }}>
                  {/* Avatar/Initials */}
                  <Box sx={{ mr: 2, display: 'flex', alignItems: 'center', justifyContent: 'center', width: 36, height: 36, borderRadius: '50%', bgcolor: '#e0e7ef', fontWeight: 700, fontSize: 18, color: '#19376D', border: '2px solid #19376D' }}>
                    {c.user?.profilePhoto ? (
                      <img
                        src={`/uploads/profile-photos/${c.user.profilePhoto}`}
                        alt="profile"
                        style={{ width: 36, height: 36, borderRadius: '50%', objectFit: 'cover' }}
                      />
                    ) : (
                      (c.user?.name ? c.user.name[0].toUpperCase() : 'A')
                    )}
                  </Box>
                  <Box sx={{ flex: 1 }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Typography fontWeight={600} fontSize={16} color="#19376D">{c.user?.name || 'Anonim'}</Typography>
                      <Typography fontSize={12} color="#888" ml={1}>{c.createdAt ? new Date(c.createdAt).toLocaleString() : ''}</Typography>
                    </Box>
                    <Typography fontSize={15} color="#222" mt={0.5}>{c.text}</Typography>
                    <Button size="small" sx={{ fontSize: 13, color: '#3b82f6', textTransform: 'none', fontWeight: 600, mt: 0.5 }} variant="text">Reply</Button>
                  </Box>
                </Box>
              ))
            )}
          </Box>
          {/* Show login button at the very bottom if not logged in */}
          {!token && (
            <Box sx={{ width: '100%', display: 'flex', justifyContent: 'center', mt: 3 }}>
              <Button variant="contained" color="primary" sx={{ borderRadius: 2, fontWeight: 700, px: 4, py: 1.2, boxShadow: 2, textTransform: 'none', fontSize: 16 }} href="/login">Yorum yapmak için giriş yapın</Button>
            </Box>
          )}
        </Box>
        </Box>
      </Box>
    </Box>
  );
};

export default Rankings;
