import React, { useEffect, useMemo, useState } from 'react';
import { Box, Typography, LinearProgress, CircularProgress, Tooltip } from '@mui/material';
import { useNavigate } from 'react-router-dom';

interface FillInTheBlanksQuestion {
  id: string;
  text: string; // uses __________ as blank placeholder
  options: string[];
  correctAnswers: string[]; // length must match number of blanks
  explanation?: string;
}

const BLANK = '__________';

const FillInTheBlanksGame: React.FC = () => {
  const navigate = useNavigate();

  // data state
  const [questions, setQuestions] = useState<FillInTheBlanksQuestion[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // game state
  const [currentIndex, setCurrentIndex] = useState(0);
  const [userAnswers, setUserAnswers] = useState<(string | null)[]>([]);
  const [availableOptions, setAvailableOptions] = useState<string[]>([]);
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);
  const [draggedItem, setDraggedItem] = useState<string | null>(null);
  const [time, setTime] = useState(0);

  // fetch questions
  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        setLoading(true);
        const res = await fetch('http://localhost:4000/api/games/fill-in-the-blanks/questions');
        if (!res.ok) throw new Error('Failed to fetch');
        const data: FillInTheBlanksQuestion[] = await res.json();
        if (!Array.isArray(data) || data.length === 0) throw new Error('No data');
        setQuestions(data);
        initQuestion(data[0]);
        setError(null);
      } catch (e) {
        console.error(e);
        setError('Sorular yüklenemedi. Örnek soru ile devam ediliyor.');
        const sample: FillInTheBlanksQuestion = {
          id: 'sample-1',
          text: 'I __________ from Turkey. My name __________ John. She __________ a student. We __________ happy today. They __________ at home.',
          options: ['am', 'is', 'are', 'was', 'were'],
          correctAnswers: ['am', 'is', 'is', 'are', 'are'],
          explanation: 'Bu soruda "to be" fiilinin temel çekimleri test edilir.'
        };
        setQuestions([sample]);
        initQuestion(sample);
      } finally {
        setLoading(false);
      }
    };
    fetchQuestions();
  }, []);

  const current = questions[currentIndex];

  // timer
  useEffect(() => {
    if (showResults) return;
    const t = setInterval(() => setTime((s) => s + 1), 1000);
    return () => clearInterval(t);
  }, [showResults]);

  // init current question
  const initQuestion = (q: FillInTheBlanksQuestion) => {
    setUserAnswers(Array(q.correctAnswers.length).fill(null));
    setAvailableOptions([...q.options]);
    setIsSubmitted(false);
    setShowResults(false);
    setScore(0);
  };

  const blanksCount = useMemo(() => (current ? current.correctAnswers.length : 0), [current]);
  const allFilled = userAnswers.length === blanksCount && userAnswers.every((a) => a !== null);

  // drag events
  const handleDragStart = (e: React.DragEvent, option: string) => {
    if (isSubmitted) return;
    setDraggedItem(option);
    e.dataTransfer.effectAllowed = 'move';
  };
  const handleDragOver = (e: React.DragEvent) => {
    e.preventDefault();
    e.dataTransfer.dropEffect = 'move';
  };
  const handleDrop = (e: React.DragEvent, blankIndex: number) => {
    e.preventDefault();
    if (!draggedItem || isSubmitted) return;
    const prev = userAnswers[blankIndex];
    const next = [...userAnswers];
    if (prev) setAvailableOptions((opts) => [...opts, prev]);
    next[blankIndex] = draggedItem;
    setUserAnswers(next);
    setAvailableOptions((opts) => opts.filter((o) => o !== draggedItem));
    setDraggedItem(null);
  };
  const clearBlank = (blankIndex: number) => {
    if (isSubmitted) return;
    const prev = userAnswers[blankIndex];
    if (!prev) return;
    setAvailableOptions((opts) => [...opts, prev]);
    const next = [...userAnswers];
    next[blankIndex] = null;
    setUserAnswers(next);
  };

  const handleSubmit = () => {
    if (!current) return;
    if (!allFilled) return; // hard guard
    setIsSubmitted(true);
    let correct = 0;
    current.correctAnswers.forEach((ans, i) => {
      if (userAnswers[i] === ans) correct++;
    });
    setScore(correct);
    setShowResults(true);
  };

  const nextQuestion = () => {
    if (currentIndex >= questions.length - 1) return;
    const idx = currentIndex + 1;
    setCurrentIndex(idx);
    initQuestion(questions[idx]);
  };
  const prevQuestion = () => {
    if (currentIndex <= 0) return;
    const idx = currentIndex - 1;
    setCurrentIndex(idx);
    initQuestion(questions[idx]);
  };

  // render helpers
  const renderTextWithBlanks = () => {
    if (!current) return null;
    const parts = current.text.split(BLANK);
    const nodes: React.ReactNode[] = [];
    parts.forEach((part, i) => {
      if (part) {
        nodes.push(
          <span key={`t-${i}`} style={{ display: 'inline' }}>{part}</span>
        );
      }
      if (i < parts.length - 1) {
        const blankIndex = i;
        const value = userAnswers[blankIndex];
        const correct = current.correctAnswers[blankIndex];
        const isCorrect = isSubmitted && value === correct;
        const isWrong = isSubmitted && value && value !== correct;
        nodes.push(
          <Box
            key={`b-${i}`}
            onDragOver={handleDragOver}
            onDrop={(e) => handleDrop(e, blankIndex)}
            onClick={() => clearBlank(blankIndex)}
            sx={{
              display: 'inline-flex', alignItems: 'center', justifyContent: 'center',
              minWidth: 120, height: 40, border: '2px solid', borderRadius: 1.5, m: '0 4px', px: 1.5,
              cursor: value && !isSubmitted ? 'pointer' : 'default',
              backgroundColor: isSubmitted
                ? (isCorrect ? '#e6ffe6' : isWrong ? '#ffe6e6' : 'rgba(0, 184, 148, 0.08)')
                : value ? 'rgba(0, 184, 148, 0.18)' : 'rgba(0, 184, 148, 0.08)',
              borderColor: isSubmitted
                ? (isCorrect ? '#43ea7c' : isWrong ? '#e74c3c' : 'rgba(0, 184, 148, 0.3)')
                : value ? '#00b894' : 'rgba(0, 184, 148, 0.3)',
              transition: 'all .2s ease',
            }}
          >
            <Typography
              variant="body1"
              sx={{ fontWeight: value ? 700 : 400, color: isSubmitted ? (isCorrect ? '#2e7d32' : isWrong ? '#c62828' : '#2c3e50') : value ? '#00b894' : '#666' }}
            >
              {value || blankIndex + 1}
            </Typography>
          </Box>
        );
      }
    });
    return <Box sx={{ display: 'inline', lineHeight: 2.4, fontSize: '1.15rem' }}>{nodes}</Box>;
  };

  if (!current) {
    if (loading) {
      return (
        <Box sx={{ minHeight: '100vh', bgcolor: '#b2ebf2', display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
          <CircularProgress size={56} sx={{ color: '#00b894', mb: 1.5 }} />
          <Typography sx={{ color: '#00b894', fontWeight: 600 }}>Sorular yükleniyor…</Typography>
        </Box>
      );
    }
    return (
      <Box sx={{ minHeight: '100vh', bgcolor: '#b2ebf2', display: 'flex', alignItems: 'center', justifyContent: 'center', textAlign: 'center', px: 2 }}>
        <Box sx={{ p: 4, borderRadius: 3, background: 'linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%)', boxShadow: '0 12px 28px rgba(0,0,0,.1)' }}>
          <Typography sx={{ color: '#c62828', fontWeight: 600, mb: 2 }}>{error || 'Soru bulunamadı.'}</Typography>
          <button onClick={() => navigate('/questions')} style={{
            background: 'linear-gradient(135deg, #00b894 0%, #00cec9 100%)', color: '#fff', fontWeight: 700, border: 'none', borderRadius: 12, padding: '10px 18px', cursor: 'pointer'
          }}>Geri Dön</button>
        </Box>
      </Box>
    );
  }

  return (
    <Box sx={{ minHeight: '100vh', bgcolor: '#b2ebf2', display: 'flex', flexDirection: 'column', alignItems: 'center', px: 2, pt: 1.5 }}>
      <Box sx={{ width: '100%', maxWidth: 800, background: 'linear-gradient(145deg, #ffffff 0%, #f8f9fa 100%)', borderRadius: 4, boxShadow: '0 20px 40px rgba(0,0,0,.1)', p: 4, mt: 1.5 }}>
        {/* top bar */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <button onClick={() => navigate('/questions')} style={{
            background: 'transparent', border: '2px solid rgba(0, 184, 148, 0.3)', color: '#00b894', borderRadius: 12, padding: '8px 12px', cursor: 'pointer'
          }}>← Geri</button>
          <Box sx={{ flex: 1 }} />
          <Typography sx={{ color: '#00b894', fontWeight: 700, px: 1.5, py: 1, borderRadius: 2, border: '1px solid rgba(0, 184, 148, 0.25)', bgcolor: 'rgba(0, 184, 148, 0.08)' }}>
            {Math.floor(time / 60)}:{String(time % 60).padStart(2, '0')}
          </Typography>
        </Box>

        <Typography variant="h5" sx={{ fontWeight: 800, textAlign: 'center', color: '#2c3e50', mb: 1 }}>BOŞLUK DOLDURMA OYUNU</Typography>
        <LinearProgress variant="determinate" value={((currentIndex + 1) / questions.length) * 100} sx={{ height: 10, borderRadius: 5, mb: 2, bgcolor: 'rgba(0, 184, 148, 0.1)', '& .MuiLinearProgress-bar': { background: 'linear-gradient(90deg, #00b894 0%, #00cec9 100%)' } }} />
        <Typography sx={{ textAlign: 'center', color: '#2c3e50', fontWeight: 600, mb: 2 }}>Soru {currentIndex + 1} / {questions.length}</Typography>

        {/* text */}
        <Box sx={{ p: 3, mb: 3, bgcolor: 'rgba(0, 184, 148, 0.08)', borderRadius: 2, border: '1px solid rgba(0, 184, 148, 0.2)' }}>
          {renderTextWithBlanks()}
        </Box>

        {/* options */}
        <Box sx={{ p: 3, mb: 3, bgcolor: 'rgba(116,185,255,.08)', borderRadius: 2, border: '1px solid rgba(116,185,255,.25)' }}>
          <Typography sx={{ fontWeight: 700, mb: 1.5, color: '#2c3e50' }}>Seçenekler:</Typography>
          <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 12 }}>
            {availableOptions.map((opt, i) => (
              <Box key={`opt-${i}`} draggable={!isSubmitted} onDragStart={(e) => handleDragStart(e, opt)}
                sx={{ px: 2, py: 1, color: '#fff', borderRadius: 1, userSelect: 'none', cursor: isSubmitted ? 'default' : 'grab', background: 'linear-gradient(135deg, #74b9ff 0%, #0984e3 100%)', boxShadow: '0 4px 12px rgba(116,185,255,.35)', opacity: isSubmitted ? .7 : 1 }}>
                <Typography sx={{ fontWeight: 700 }}>{opt}</Typography>
              </Box>
            ))}
          </Box>
        </Box>

        {/* results */}
        {showResults && (
          <Box sx={{ p: 3, mb: 3, borderRadius: 2, border: `2px solid ${score === blanksCount ? '#43ea7c' : '#ffc107'}`, bgcolor: score === blanksCount ? '#e6ffe6' : '#fff8e1' }}>
            <Typography sx={{ fontWeight: 700, color: score === blanksCount ? '#2e7d32' : '#8d6e63', mb: 1.5 }}>Sonuç: {score}/{blanksCount} doğru</Typography>
            {current.correctAnswers.map((ans, i) => (
              <Typography key={i} sx={{ color: '#2c3e50' }}>
                Boşluk {i + 1}: <strong>{ans}</strong>{userAnswers[i] && userAnswers[i] !== ans ? ` (Siz: ${userAnswers[i]})` : ''}
              </Typography>
            ))}
            {current.explanation && (
              <Typography sx={{ mt: 1.5, fontStyle: 'italic', color: '#666' }}>{current.explanation}</Typography>
            )}
          </Box>
        )}

        {/* navigation: Left / Center / Right */}
        <Box sx={{ display: 'grid', gridTemplateColumns: '1fr auto 1fr', alignItems: 'center', gap: 2, mt: 2 }}>
          {/* Left: Önceki */}
          <button
            onClick={prevQuestion}
            disabled={currentIndex === 0}
            style={{
              background: currentIndex === 0 ? 'linear-gradient(135deg,#e0e0e0,#bdbdbd)' : 'linear-gradient(135deg,#00b894,#00cec9)',
              color: currentIndex === 0 ? '#666' : '#fff', fontWeight: 700, fontSize: 16, border: 'none', borderRadius: 12,
              padding: '12px 20px', cursor: currentIndex === 0 ? 'not-allowed' : 'pointer', opacity: currentIndex === 0 ? .7 : 1,
              minWidth: 108, justifySelf: 'start', boxShadow: currentIndex === 0 ? 'none' : '0 4px 12px rgba(0,184,148,.3)'
            }}
          >
            ← Önceki
          </button>

          {/* Center: Kontrol Et (pre-submit) or Bitir (last after submit) */}
          {!isSubmitted ? (
            <Tooltip title={!allFilled ? 'Lütfen tüm boşlukları doldurmalısınız.' : ''} arrow disableHoverListener={allFilled}>
              <span>
                <button
                  onClick={handleSubmit}
                  disabled={!allFilled}
                  style={{
                    background: 'linear-gradient(135deg,#00b894,#00cec9)', color: '#fff', fontWeight: 800, fontSize: 16,
                    border: 'none', borderRadius: 12, padding: '12px 24px', cursor: !allFilled ? 'not-allowed' : 'pointer',
                    minWidth: 130, justifySelf: 'center', opacity: !allFilled ? .7 : 1, boxShadow: '0 4px 12px rgba(0,184,148,.3)'
                  }}
                >
                  Kontrol Et
                </button>
              </span>
            </Tooltip>
          ) : (
            currentIndex === questions.length - 1 ? (
              <button
                onClick={() => navigate('/questions')}
                style={{
                  background: 'linear-gradient(135deg,#74b9ff,#0984e3)', color: '#fff', fontWeight: 800, fontSize: 16,
                  border: 'none', borderRadius: 12, padding: '12px 30px', cursor: 'pointer', minWidth: 130,
                  justifySelf: 'center', boxShadow: '0 4px 12px rgba(116,185,255,.35)'
                }}
              >
                Bitir
              </button>
            ) : (
              <span />
            )
          )}

          {/* Right: Sonraki/İleri */}
          <button
            onClick={() => {
              if (currentIndex < questions.length - 1) nextQuestion();
            }}
            disabled={currentIndex === questions.length - 1}
            style={{
              background: currentIndex === questions.length - 1 ? 'linear-gradient(135deg,#e0e0e0,#bdbdbd)' : 'linear-gradient(135deg,#74b9ff,#0984e3)',
              color: '#fff', fontWeight: 700, fontSize: 16, border: 'none', borderRadius: 12, padding: '12px 20px',
              cursor: currentIndex === questions.length - 1 ? 'not-allowed' : 'pointer', minWidth: 108, justifySelf: 'end',
              opacity: currentIndex === questions.length - 1 ? .7 : 1, boxShadow: '0 4px 12px rgba(0,0,0,.15)'
            }}
          >
            {isSubmitted ? 'İleri →' : 'Sonraki →'}
          </button>
        </Box>
      </Box>
    </Box>
  );
};

export default FillInTheBlanksGame;
