  { label: 'Bo≈üluk Doldurma Oyunu', url: '/bosluk-doldurma', keywords: ['fill in the blanks', 'bosluk doldurma', 'paragraph'] },
  { label: 'Essay Writing', url: '/essay-writing', keywords: ['essay', 'yapay zeka', 'degerlendirme'] },
