import React, { useState } from 'react';
import { Card, Tabs, Tab, Box, Typography, TextField, List, ListItem, ListItemText } from '@mui/material';


type Topic = { title: string; summary: string; example: string };
type LevelKey = 'A1' | 'A2' | 'B1' | 'B2';
const topicsData: Record<LevelKey, Topic[]> = {
  A1: [
    { title: 'Simple Present Tense', summary: 'Günlük rutinleri ve genel doğruları anlatmak için kullanılır.', example: 'I go to school every day.' },
    { title: 'There is / There are', summary: 'Bir şeyin varlığını belirtmek için kullanılır.', example: 'There is a book on the table.' },
  ],
  A2: [
    { title: 'Past Simple Tense', summary: 'Geçmişteki olayları anlatmak için kullanılır.', example: 'She visited Paris last year.' },
  ],
  B1: [
    { title: 'Present Perfect Tense', summary: 'Geçmişte başlayıp etkisi süren olaylar için kullanılır.', example: 'I have lived here for five years.' },
  ],
  B2: [
    { title: 'Passive Voice', summary: 'Eylemin kim tarafından yapıldığının önemli olmadığı durumlarda kullanılır.', example: 'The cake was eaten.' },
  ],
};

const levels = ['A1', 'A2', 'B1', 'B2'];

const TopicsPage: React.FC = () => {
  const [selectedLevel, setSelectedLevel] = useState('A1');
  const [search, setSearch] = useState('');

  const filteredTopics = topicsData[selectedLevel as LevelKey].filter(
    (topic: Topic) =>
      topic.title.toLowerCase().includes(search.toLowerCase()) ||
      topic.summary.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
      {/* Sağ-sol boşluk için Box */}
      <Box sx={{ flex: 1 }} />
      <Card sx={{ minWidth: 350, maxWidth: 600, width: '100%', p: 3, boxShadow: 4, borderRadius: 4, bgcolor: '#f8fcff' }}>
        <Tabs
          value={selectedLevel}
          onChange={(_, val) => setSelectedLevel(val)}
          variant="fullWidth"
          sx={{ mb: 2 }}
        >
          {levels.map(level => (
            <Tab key={level} value={level} label={level} />
          ))}
        </Tabs>
        <TextField
          fullWidth
          variant="outlined"
          placeholder="Konu ara..."
          value={search}
          onChange={e => setSearch(e.target.value)}
          sx={{ mb: 2 }}
        />
        <List>
          {filteredTopics.map((topic: Topic, idx: number) => (
            <ListItem key={idx} sx={{ mb: 2, alignItems: 'flex-start' }}>
              <ListItemText
                primary={<Typography variant="h6">{topic.title}</Typography>}
                secondary={
                  <>
                    <Typography variant="body2" color="text.secondary">{topic.summary}</Typography>
                    <Typography variant="body2" sx={{ mt: 1, fontStyle: 'italic', color: '#1976d2' }}>Örnek: {topic.example}</Typography>
                  </>
                }
              />
            </ListItem>
          ))}
          {filteredTopics.length === 0 && (
            <Typography color="text.secondary" sx={{ mt: 2, textAlign: 'center' }}>
              Bu seviyede aradığınız konu bulunamadı.
            </Typography>
          )}
        </List>
      </Card>
      <Box sx={{ flex: 1 }} />
    </Box>
  );
};

export default TopicsPage;
